public void parseEmbedded(InputStream inputStream, ContentHandler contentHandler, Metadata metadata, boolean b) throws SAXException, IOException {
    ByteArrayOutputStream bos = new ByteArrayOutputStream();
    IOUtils.copy(inputStream, bos);
    byte[] data = bos.toByteArray();

    String name = metadata.get(TikaMetadataKeys.RESOURCE_NAME_KEY);
    String contentType = metadata.get(org.apache.tika.metadata.HttpHeaders.CONTENT_TYPE);

    if (name == null) {
        name = Integer.toString(count.intValue());
    }

    if (!name.contains(".") && contentType != null) {
        try {
            String ext = TikaResource.getConfig().getMimeRepository().forName(contentType).getExtension();

            if (ext != null) {
                name += ext;
            }
        } catch (MimeTypeException e) {
            LOG.warn("Unexpected MimeTypeException", e);
        }
    }

    if ("application/vnd.openxmlformats-officedocument.oleObject".equals(contentType)) {
        POIFSFileSystem poifs = new POIFSFileSystem(new ByteArrayInputStream(data));
        OfficeParser.POIFSDocumentType type = OfficeParser.POIFSDocumentType.detectType(poifs);

        if (type == OfficeParser.POIFSDocumentType.OLE10_NATIVE) {
            try {
                Ole10Native ole = Ole10Native.createFromEmbeddedOleObject(poifs);
                if (ole.getDataSize() > 0) {
                    String label = ole.getLabel();

                    if (label.startsWith("ole-")) {
                        label = Integer.toString(count.intValue()) + '-' + label;
                    }

                    name = label;

                    data = ole.getDataBuffer();
                }
            } catch (Ole10NativeException ex) {
                LOG.warn("Skipping invalid part", ex);
            }
        } else {
            name += '.' + type.getExtension();
        }
    }

    final String finalName = name;

    if (data.length > 0) {
        zout.put(finalName, data);

        count.increment();
    } else {
        if (inputStream instanceof TikaInputStream) {
            TikaInputStream tin = (TikaInputStream) inputStream;

            if (tin.getOpenContainer() != null && tin.getOpenContainer() instanceof DirectoryEntry) {
                POIFSFileSystem fs = new POIFSFileSystem();
                copy((DirectoryEntry) tin.getOpenContainer(), fs.getRoot());
                ByteArrayOutputStream bos2 = new ByteArrayOutputStream();
                fs.writeFilesystem(bos2);
                bos2.close();

                zout.put(finalName, bos2.toByteArray());
            }
        }
    }
}

private void extractFiles(File archive, File destination, Engine engine) throws AnalysisException {
    if (archive != null && destination != null) {
        String archiveExt = FileUtils.getFileExtension(archive.getName());
        if (archiveExt == null) {
            return;
        }
        archiveExt = archiveExt.toLowerCase();

        final FileInputStream fis;
        try {
            fis = new FileInputStream(archive);
        } catch (FileNotFoundException ex) {
            final String msg = String.format("Error extracting file `%s`: %s", archive.getAbsolutePath(), ex.getMessage());
            LOGGER.debug(msg, ex);
            throw new AnalysisException(msg);
        }
        BufferedInputStream in = null;
        ZipArchiveInputStream zin = null;
        TarArchiveInputStream tin = null;
        GzipCompressorInputStream gin = null;
        BZip2CompressorInputStream bzin = null;
        try {
            if (KNOWN_ZIP_EXT.contains(archiveExt)) {
                in = new BufferedInputStream(fis);
                ensureReadableJar(archiveExt, in);
                zin = new ZipArchiveInputStream(in);
                extractArchive(zin, destination, engine);
            } else if ("tar".equals(archiveExt)) {
                in = new BufferedInputStream(fis);
                tin = new TarArchiveInputStream(in);
                extractArchive(tin, destination, engine);
            } else if ("gz".equals(archiveExt) || "tgz".equals(archiveExt)) {
                final String uncompressedName = GzipUtils.getUncompressedFilename(archive.getName());
                final File f = new File(destination, uncompressedName);
                if (engine.accept(f)) {
                    in = new BufferedInputStream(fis);
                    gin = new GzipCompressorInputStream(in);
                    decompressFile(gin, f);
                }
            } else if ("bz2".equals(archiveExt) || "tbz2".equals(archiveExt)) {
                final String uncompressedName = BZip2Utils.getUncompressedFilename(archive.getName());
                final File f = new File(destination, uncompressedName);
                if (engine.accept(f)) {
                    in = new BufferedInputStream(fis);
                    bzin = new BZip2CompressorInputStream(in);
                    decompressFile(bzin, f);
                }
            }
        } catch (ArchiveExtractionException ex) {
            LOGGER.warn("Exception extracting archive '{}'.", archive.getName());
            LOGGER.debug("", ex);
        } catch (IOException ex) {
            LOGGER.warn("Exception reading archive '{}'.", archive.getName());
            LOGGER.debug("", ex);
        } finally {
            //overly verbose and not needed... but keeping it anyway due to
            //having issue with file handles being left open
            FileUtils.close(fis);
            FileUtils.close(in);
            FileUtils.close(zin);
            FileUtils.close(tin);
            FileUtils.close(gin);
            FileUtils.close(bzin);
        }
    }
}

private void extractArchive(ArchiveInputStream input, File destination, Engine engine) throws ArchiveExtractionException {
    ArchiveEntry entry;
    try {
        while ((entry = input.getNextEntry()) != null) {
            final File file = new File(destination, entry.getName());
            if (entry.isDirectory()) {
                if (!file.exists() && !file.mkdirs()) {
                    final String msg = String.format("Unable to create directory '%s'.", file.getAbsolutePath());
                    throw new AnalysisException(msg);
                }
            } else if (engine.accept(file)) {
                extractAcceptedFile(input, file);
            }
        }
    } catch (IOException | AnalysisException ex) {
        throw new ArchiveExtractionException(ex);
    } finally {
        FileUtils.close(input);
    }
}

public static void extractFiles(File archive, File extractTo) throws ExtractionException {
    extractFiles(archive, extractTo, null);
}

public static void extractGzip(File file) throws FileNotFoundException, IOException {
    final String originalPath = file.getPath();
    final File gzip = new File(originalPath + ".gz");
    if (gzip.isFile() && !gzip.delete()) {
        LOGGER.debug("Failed to delete initial temporary file when extracting 'gz' {}", gzip.toString());
        gzip.deleteOnExit();
    }
    if (!file.renameTo(gzip)) {
        throw new IOException("Unable to rename '" + file.getPath() + "'");
    }
    final File newFile = new File(originalPath);
    try (FileInputStream fis = new FileInputStream(gzip);
            GZIPInputStream cin = new GZIPInputStream(fis);
            FileOutputStream out = new FileOutputStream(newFile)) {
        IOUtils.copy(cin, out);
    } finally {
        if (gzip.isFile() && !org.apache.commons.io.FileUtils.deleteQuietly(gzip)) {
            LOGGER.debug("Failed to delete temporary file when extracting 'gz' {}", gzip.toString());
            gzip.deleteOnExit();
        }
    }
}

private static void extractArchive(ArchiveInputStream input,
        File destination, FilenameFilter filter)
        throws ArchiveExtractionException {
    ArchiveEntry entry;
    try {
        while ((entry = input.getNextEntry()) != null) {
            if (entry.isDirectory()) {
                final File dir = new File(destination, entry.getName());
                if (!dir.exists() && !dir.mkdirs()) {
                    final String msg = String.format(
                            "Unable to create directory '%s'.",
                            dir.getAbsolutePath());
                    throw new AnalysisException(msg);
                }
            } else {
                extractFile(input, destination, filter, entry);
            }
        }
    } catch (IOException | AnalysisException ex) {
        throw new ArchiveExtractionException(ex);
    } finally {
        FileUtils.close(input);
    }
}

public void handle(RoutingContext context) {
  HttpServerRequest request = context.request();
  if (request.method() != HttpMethod.GET && request.method() != HttpMethod.HEAD) {
    if (log.isTraceEnabled()) log.trace("Not GET or HEAD so ignoring request");
    context.next();
  } else {
    String path = Utils.removeDots(Utils.urlDecode(context.normalisedPath(), false));
    // if the normalized path is null it cannot be resolved
    if (path == null) {
      log.warn("Invalid path: " + context.request().path());
      context.next();
      return;
    }

    // only root is known for sure to be a directory. all other directories must be identified as such.
    if (!directoryListing && "/".equals(path)) {
      path = indexPage;
    }

    // can be called recursive for index pages
    sendStatic(context, path);

  }
}

private void sendStatic(RoutingContext context, String path) {

  String file = null;

  if (!includeHidden) {
    file = getFile(path, context);
    int idx = file.lastIndexOf('/');
    String name = file.substring(idx + 1);
    if (name.length() > 0 && name.charAt(0) == '.') {
      // skip
      context.next();
      return;
    }
  }

  // Look in cache
  CacheEntry entry;
  if (cachingEnabled) {
    entry = propsCache().get(path);
    if (entry != null) {
      HttpServerRequest request = context.request();
      if ((filesReadOnly || !entry.isOutOfDate()) && entry.shouldUseCached(request)) {
        context.response().setStatusCode(NOT_MODIFIED.code()).end();
        return;
      }
    }
  }

  if (file == null) {
    file = getFile(path, context);
  }

  final String sfile = file;

  // verify if the file exists
  isFileExisting(context, sfile, exists -> {
    if (exists.failed()) {
      context.fail(exists.cause());
      return;
    }

    // file does not exist, continue...
    if (!exists.result()) {
      context.next();
      return;
    }

    // Need to read the props from the filesystem
    getFileProps(context, sfile, res -> {
      if (res.succeeded()) {
        FileProps fprops = res.result();
        if (fprops == null) {
          // File does not exist
          context.next();
        } else if (fprops.isDirectory()) {
          sendDirectory(context, path, sfile);
        } else {
          propsCache().put(path, new CacheEntry(fprops, System.currentTimeMillis()));
          sendFile(context, sfile, fprops);
        }
      } else {
        context.fail(res.cause());
      }
    });
  });
}

