public final Resource forFile(String relativePath)
{
    assert relativePath != null;

    List<String> terms = CollectionFactory.newList();

    for (String term : getFolder().split("/"))
    {
        terms.add(term);
    }

    for (String term : relativePath.split("/"))
    {
        // This will occur if the relative path contains sequential slashes

        if (term.equals("") || term.equals("."))
        {
            continue;
        }

        if (term.equals(".."))
        {
            if (terms.isEmpty())
            {
                throw new IllegalStateException(String.format("Relative path '%s' for %s would go above root.", relativePath, this));
            }

            terms.remove(terms.size() - 1);

            continue;
        }

        // TODO: term blank or otherwise invalid?
        // TODO: final term should not be "." or "..", or for that matter, the
        // name of a folder, since a Resource should be a file within
        // a folder.

        terms.add(term);
    }

    StringBuilder path = new StringBuilder(100);
    String sep = "";

    for (String term : terms)
    {
        path.append(sep).append(term);
        sep = "/";
    }

    return createResource(path.toString());
}

public void testMessageDecodingPerformance() throws Exception {

    byte[] CONTENTS = new byte[MESSAGE_SIZE];
    for (int i = 0; i < MESSAGE_SIZE; i++) {
        CONTENTS[i] = 'a';
    }

    PUBLISH publish = new PUBLISH();

    publish.dup(false);
    publish.messageId((short) 127);
    publish.qos(QoS.AT_LEAST_ONCE);
    publish.payload(new Buffer(CONTENTS));
    publish.topicName(new UTF8Buffer("TOPIC"));

    DataByteArrayOutputStream output = new DataByteArrayOutputStream();
    wireFormat.marshal(publish.encode(), output);
    Buffer marshalled = output.toBuffer();

    long startTime = System.currentTimeMillis();

    for (int i = 0; i < ITERATIONS; ++i) {
        DataByteArrayInputStream input = new DataByteArrayInputStream(marshalled);
        codec.parse(input, marshalled.length());

        assertTrue(!frames.isEmpty());
        publish = new PUBLISH().decode(frames.get(0));
        frames.clear();
    }

    long duration = System.currentTimeMillis() - startTime;

    LOG.info("Total time to process: {}", TimeUnit.MILLISECONDS.toSeconds(duration));
}

public int doWikiStartTag()
    throws IOException
{
    String     pageName = m_pageName;
    boolean    isattachment = false;

    if( m_pageName == null )
    {
        WikiPage p = m_wikiContext.getPage();

        if( p != null )
        {
            pageName = p.getName();

            isattachment = p instanceof Attachment;
        }
        else
        {
            return SKIP_BODY;
        }
    }

    JspWriter out = pageContext.getOut();
    String url;
    String linkclass;

    if( isattachment )
    {
        url = m_wikiContext.getURL(WikiContext.ATTACH,pageName,
                                   (getVersion() != null) ? "version="+getVersion() : null );
        linkclass = "attachment";
    }
    else
    {
    	StringBuilder params = new StringBuilder();
        if( getVersion() != null ) params.append( "version="+getVersion() );
        if( getTemplate() != null ) params.append( (params.length()>0?"&amp;":"") + "skin="+getTemplate() );

        url = m_wikiContext.getURL( WikiContext.VIEW, pageName,
                                    params.toString() );
        linkclass = "wikipage";
    }

    switch( m_format )
    {
      case ANCHOR:
        out.print("<a class=\""+linkclass+"\" href=\""+url+"\" accesskey=\"" 
                      + m_accesskey + "\" title=\"" + m_title + "\">");
        break;
      case URL:
        out.print( url );
        break;
    }

    return EVAL_BODY_INCLUDE;
}

