private Element handleHyperlinks( String linktext, int pos )
{
    ResourceBundle rb = Preferences.getBundle( m_context, InternationalizationManager.CORE_BUNDLE );

    StringBuilder sb = new StringBuilder(linktext.length()+80);

    if( m_linkParsingOperations.isAccessRule( linktext ) )
    {
        return handleAccessRule( linktext );
    }

    if( m_linkParsingOperations.isMetadata( linktext ) )
    {
        return handleMetadata( linktext );
    }

    if( m_linkParsingOperations.isPluginLink( linktext ) )
    {
        try
        {
            PluginContent pluginContent = PluginContent.parsePluginLine( m_context, linktext, pos );
            //
            //  This might sometimes fail, especially if there is something which looks
            //  like a plugin invocation but is really not.
            //
            if( pluginContent != null )
            {
                addElement( pluginContent );

                pluginContent.executeParse( m_context );
            }
        }
        catch( PluginException e )
        {
            log.info( m_context.getRealPage().getWiki() + " : " + m_context.getRealPage().getName() + " - Failed to insert plugin: " + e.getMessage() );
            //log.info( "Root cause:",e.getRootThrowable() );
            if( !m_wysiwygEditorMode )
            {
                ResourceBundle rbPlugin = Preferences.getBundle( m_context, WikiPlugin.CORE_PLUGINS_RESOURCEBUNDLE );
                return addElement( makeError( MessageFormat.format( rbPlugin.getString( "plugin.error.insertionfailed" ),
                		                                            m_context.getRealPage().getWiki(),
                		                                            m_context.getRealPage().getName(),
                		                                            e.getMessage() ) ) );
            }
        }

        return m_currentElement;
    }

    try
    {
        LinkParser.Link link = m_linkParser.parse(linktext);
        linktext       = link.getText();
        String linkref = link.getReference();

        //
        //  Yes, we now have the components separated.
        //  linktext = the text the link should have
        //  linkref  = the url or page name.
        //
        //  In many cases these are the same.  [linktext|linkref].
        //
        if( m_linkParsingOperations.isVariableLink( linktext ) )
        {
            Content el = new VariableContent(linktext);

            addElement( el );
        }
        else if( m_linkParsingOperations.isExternalLink( linkref ) )
        {
            // It's an external link, out of this Wiki

            callMutatorChain( m_externalLinkMutatorChain, linkref );

            if( m_linkParsingOperations.isImageLink( linkref ) )
            {
                handleImageLink( linkref, linktext, link.hasReference() );
            }
            else
            {
                makeLink( EXTERNAL, linkref, linktext, null, link.getAttributes() );
                addElement( outlinkImage() );
            }
        }
        else if( link.isInterwikiLink() )
        {
            // It's an interwiki link
            // InterWiki links also get added to external link chain
            // after the links have been resolved.

            // FIXME: There is an interesting issue here:  We probably should
            //        URLEncode the wikiPage, but we can't since some of the
            //        Wikis use slashes (/), which won't survive URLEncoding.
            //        Besides, we don't know which character set the other Wiki
            //        is using, so you'll have to write the entire name as it appears
            //        in the URL.  Bugger.

            String extWiki  = link.getExternalWiki();
            String wikiPage = link.getExternalWikiPage();

            if( m_wysiwygEditorMode )
            {
                makeLink( INTERWIKI, extWiki + ":" + wikiPage, linktext, null, link.getAttributes() );
            }
            else
            {
                String urlReference = m_engine.getInterWikiURL( extWiki );

                if( urlReference != null )
                {
                    urlReference = TextUtil.replaceString( urlReference, "%s", wikiPage );
                    urlReference = callMutatorChain( m_externalLinkMutatorChain, urlReference );

                    if( m_linkParsingOperations.isImageLink(urlReference) )
                    {
                        handleImageLink( urlReference, linktext, link.hasReference() );
                    }
                    else
                    {
                        makeLink( INTERWIKI, urlReference, linktext, null, link.getAttributes() );
                    }

                    if( m_linkParsingOperations.isExternalLink(urlReference) )
                    {
                        addElement( outlinkImage() );
                    }
                }
                else
                {
                    Object[] args = { extWiki };
                    addElement( makeError( MessageFormat.format( rb.getString( "markupparser.error.nointerwikiref" ), args ) ) );
                }
            }
        }
        else if( linkref.startsWith("#") )
        {
            // It defines a local footnote
            makeLink( LOCAL, linkref, linktext, null, link.getAttributes() );
        }
        else if( TextUtil.isNumber( linkref ) )
        {
            // It defines a reference to a local footnote
            makeLink( LOCALREF, linkref, linktext, null, link.getAttributes() );
        }
        else
        {
            int hashMark = -1;

            //
            //  Internal wiki link, but is it an attachment link?
            //
            String attachment = m_engine.getAttachmentManager().getAttachmentInfoName( m_context, linkref );
            if( attachment != null )
            {
                callMutatorChain( m_attachmentLinkMutatorChain, attachment );

                if( m_linkParsingOperations.isImageLink( linkref ) )
                {
                    attachment = m_context.getURL( WikiContext.ATTACH, attachment );
                    sb.append( handleImageLink( attachment, linktext, link.hasReference() ) );
                }
                else
                {
                    makeLink( ATTACHMENT, attachment, linktext, null, link.getAttributes() );
                }
            }
            else if( (hashMark = linkref.indexOf('#')) != -1 )
            {
                // It's an internal Wiki link, but to a named section

                String namedSection = linkref.substring( hashMark+1 );
                linkref = linkref.substring( 0, hashMark );

                linkref = MarkupParser.cleanLink( linkref );

                callMutatorChain( m_localLinkMutatorChain, linkref );

                String matchedLink = m_linkParsingOperations.linkIfExists( linkref );
                if( matchedLink != null ) {
                    String sectref = "section-"+m_engine.encodeName(matchedLink+"-"+wikifyLink(namedSection));
                    sectref = sectref.replace('%', '_');
                    makeLink( READ, matchedLink, linktext, sectref, link.getAttributes() );
                } else {
                    makeLink( EDIT, linkref, linktext, null, link.getAttributes() );
                }
            }
            else
            {
                // It's an internal Wiki link
                linkref = MarkupParser.cleanLink( linkref );

                callMutatorChain( m_localLinkMutatorChain, linkref );

                String matchedLink = m_linkParsingOperations.linkIfExists( linkref );
                if( matchedLink != null ) {
                    makeLink( READ, matchedLink, linktext, null, link.getAttributes() );
                } else {
                    makeLink( EDIT, linkref, linktext, null, link.getAttributes() );
                }
            }
        }
    }
    catch( ParseException e )
    {
        log.info("Parser failure: ",e);
        Object[] args = { e.getMessage() };
        addElement( makeError( MessageFormat.format( rb.getString( "markupparser.error.parserfailure" ), args ) ) );
    }

    return m_currentElement;
}

private Element handleDiv( boolean newLine )
    throws IOException
{
    int ch = nextToken();
    Element el = null;

    if( ch == '%' )
    {
        String style = null;
        String clazz = null;

        ch = nextToken();

        //
        //  Style or class?
        //
        if( ch == '(' )
        {
            style = readBraceContent('(',')');
        }
        else if( Character.isLetter( (char) ch ) )
        {
            pushBack( ch );
            clazz = readUntil( " \t\n\r" );
            //Note: ref.https://www.w3.org/TR/CSS21/syndata.html#characters
            //CSS Classnames can contain only the characters [a-zA-Z0-9] and
            //ISO 10646 characters U+00A0 and higher, plus the "-" and the "_".
            //They cannot start with a digit, two hyphens, or a hyphen followed by a digit.

            //(1) replace '.' by spaces, allowing multiple classnames on a div or span
            //(2) remove any invalid character
            if( clazz != null){

                clazz = clazz.replace('.', ' ')
                             .replaceAll("[^\\s-_\\w\\x200-\\x377]+","");

            }
            ch = nextToken();

            //
            //  Pop out only spaces, so that the upcoming EOL check does not check the
            //  next line.
            //
            if( ch == '\n' || ch == '\r' )
            {
                pushBack(ch);
            }
        }
        else
        {
            //
            // Anything else stops.
            //

            pushBack(ch);

            try
            {
                Boolean isSpan = m_styleStack.pop();

                if( isSpan == null )
                {
                    // Fail quietly
                }
                else if( isSpan.booleanValue() )
                {
                    el = popElement( "span" );
                }
                else
                {
                    el = popElement( "div" );
                }
            }
            catch( EmptyStackException e )
            {
                log.debug("Page '"+m_context.getName()+"' closes a %%-block that has not been opened.");
                return m_currentElement;
            }

            return el;
        }

        //
        //  Check if there is an attempt to do something nasty
        //

        try
        {
            style = StringEscapeUtils.unescapeHtml(style);
            if( style != null && style.indexOf("javascript:") != -1 )
            {
                log.debug("Attempt to output javascript within CSS:"+style);
                ResourceBundle rb = Preferences.getBundle( m_context, InternationalizationManager.CORE_BUNDLE );
                return addElement( makeError( rb.getString( "markupparser.error.javascriptattempt" ) ) );
            }
        }
        catch( NumberFormatException e )
        {
            //
            //  If there are unknown entities, we don't want the parser to stop.
            //
            ResourceBundle rb = Preferences.getBundle( m_context, InternationalizationManager.CORE_BUNDLE );
            String msg = MessageFormat.format( rb.getString( "markupparser.error.parserfailure"), e.getMessage() );
            return addElement( makeError( msg ) );
        }

        //
        //  Decide if we should open a div or a span?
        //
        String eol = peekAheadLine();

        if( eol.trim().length() > 0 )
        {
            // There is stuff after the class

            el = new Element("span");

            m_styleStack.push( Boolean.TRUE );
        }
        else
        {
            startBlockLevel();
            el = new Element("div");
            m_styleStack.push( Boolean.FALSE );
        }

        if( style != null ) el.setAttribute("style", style);
        if( clazz != null ) el.setAttribute("class", clazz);
        el = pushElement( el );

        return el;
    }

    pushBack(ch);

    return el;
}

private void handleLinks(WikiContext context,Collection<String> links, int depth, String pagename) {
    boolean isUL = false;
    HashSet<String> localLinkSet = new HashSet<>();  // needed to skip multiple
    // links to the same page
    localLinkSet.add(pagename);

    ArrayList<String> allLinks = new ArrayList<>();

    if( links != null )
        allLinks.addAll( links );

    if( m_formatSort ) context.getEngine().getPageManager().getPageSorter().sort( allLinks );

    for( Iterator<String> i = allLinks.iterator(); i.hasNext(); ) {
        String link = i.next() ;

        if( localLinkSet.contains( link ) ) continue; // skip multiple links to the same page
        localLinkSet.add( link );

        if( !m_engine.pageExists( link ) ) continue; // hide links to non existing pages

        if(  m_matcher.matches( link , m_excludePattern ) ) continue;
        if( !m_matcher.matches( link , m_includePattern ) ) continue;

        if( m_exists.contains( link ) ) {
            if( !m_formatCompact ) {
                if( !isUL ) {
                    isUL = true;
                    m_result.append("<ul>\n");
                }

                //See https://www.w3.org/wiki/HTML_lists  for proper nesting of UL and LI
                m_result.append("<li> " + link + "\n");

                getReferredPages( context, link, depth );  // added recursive call - on general request

                m_result.append("\n</li>\n");

            }
        } else {
            if( !isUL ) {
                isUL = true; 
                m_result.append("<ul>\n");
            }

            String href = context.getURL(WikiContext.VIEW,link);
            m_result.append("<li><a class=\"wikipage\" href=\""+ href +"\">"+link+"</a>\n" );

            m_exists.add( link );

            getReferredPages( context, link, depth );

            m_result.append("\n</li>\n");

        }
    }

    if( isUL ) {
        m_result.append("</ul>\n");
    }
}

public String execute( WikiContext context, Map<String, String> params ) throws PluginException {
    m_engine = context.getEngine();

    WikiPage         page   = context.getPage();
    if( page == null ) return "";

    // parse parameters
    String rootname = params.get( PARAM_ROOT );
    if( rootname == null ) rootname = page.getName() ;

    String format = params.get( PARAM_FORMAT );
    if( format == null) format = "";
    if( format.indexOf( "full" ) >=0 ) m_formatCompact = false ;
    if( format.indexOf( "sort" ) >=0 ) m_formatSort    = true  ;

    m_depth = TextUtil.parseIntParameter( params.get( PARAM_DEPTH ), MIN_DEPTH );
    if( m_depth > MAX_DEPTH )  m_depth = MAX_DEPTH;

    String includePattern = params.get(PARAM_INCLUDE);
    if( includePattern == null ) includePattern = ".*";

    String excludePattern = params.get(PARAM_EXCLUDE);
    if( excludePattern == null ) excludePattern = "^$";

    log.debug( "Fetching referred pages for "+ rootname +
               " with a depth of "+ m_depth +
               " with include pattern of "+ includePattern +
               " with exclude pattern of "+ excludePattern );

    //
    // do the actual work
    //
    String href  = context.getViewURL(rootname);
    String title = "ReferredPagesPlugin: depth["+m_depth+
                   "] include["+includePattern+"] exclude["+excludePattern+
                   "] format["+(m_formatCompact ? "compact" : "full") +
                   (m_formatSort ? " sort" : "") + "]";

    m_result.append("<div class=\"ReferredPagesPlugin\">\n");
    m_result.append("<a class=\"wikipage\" href=\""+ href +
                    "\" title=\"" + title +
                    "\">" + rootname + "</a>\n");
    m_exists.add(rootname);

    // pre compile all needed patterns
    // glob compiler :  * is 0..n instance of any char  -- more convenient as input
    // perl5 compiler : .* is 0..n instances of any char -- more powerful
    //PatternCompiler g_compiler = new GlobCompiler();
    PatternCompiler compiler = new Perl5Compiler();

    try
    {
        m_includePattern = compiler.compile(includePattern);

        m_excludePattern = compiler.compile(excludePattern);
    }
    catch( MalformedPatternException e )
    {
        if (m_includePattern == null )
        {
            throw new PluginException("Illegal include pattern detected.");
        }
        else if (m_excludePattern == null )
        {
            throw new PluginException("Illegal exclude pattern detected.");
        }
        else
        {
            throw new PluginException("Illegal internal pattern detected.");
        }
    }

    // go get all referred links
    getReferredPages(context,rootname, 0);

    // close and finish
    m_result.append ("</div>\n" ) ;

    return m_result.toString() ;
}

public String getString()
    throws IOException
{
    Element rootElement = m_document.getRootElement();
    processChildren( rootElement );

    m_document.setContext( m_context );

    XMLOutputter output = new XMLOutputter();

    StringWriter out = new StringWriter();

    Format fmt = Format.getRawFormat();
    fmt.setExpandEmptyElements( false );
    fmt.setLineSeparator( LINEBREAK );

    output.setFormat( fmt );
    output.outputElementContent( m_document.getRootElement(), out );

    return out.toString();
}

private Document getFragmentAsDocument(CharSequence value) {
	// using the XML parser ensures that all elements in the input are retained, also if they actually are not allowed at the given
	// location; E.g. a <td> element isn't allowed directly within the <body> element, so it would be used by the default HTML parser.
	// we need to retain it though to apply the given white list properly; See HV-873
	Document fragment = Jsoup.parse( value.toString(), baseURI, Parser.xmlParser() );
	Document document = Document.createShell( baseURI );

	// add the fragment's nodes to the body of resulting document
	Iterator<Element> nodes = fragment.children().iterator();
	while ( nodes.hasNext() ) {
		document.body().appendChild( nodes.next() );
	}

	return document;
}

public void testInvalidScriptTagIncluded() throws Exception {
	descriptorBuilder.setAttribute( "whitelistType", WhiteListType.BASIC );

	assertFalse( getSafeHtmlValidator().isValid( "Hello<script>alert('Doh')</script>World !", null ) );
}

public void testDivWithWhiteListedClassAttribute() throws Exception {
	descriptorBuilder.setAttribute( "whitelistType", WhiteListType.RELAXED );

	AnnotationDescriptor.Builder<SafeHtml.Tag> tagDescriptorBuilder = new AnnotationDescriptor.Builder<>( SafeHtml.Tag.class );
	tagDescriptorBuilder.setAttribute( "name", "div" );
	tagDescriptorBuilder.setAttribute( "attributes", new String[] { "class" } );
	SafeHtml.Tag tag = tagDescriptorBuilder.build().getAnnotation();
	descriptorBuilder.setAttribute( "additionalTagsWithAttributes", new SafeHtml.Tag[] { tag } );

	assertTrue(
			getSafeHtmlValidator().isValid( "<div class='foo'>test</div>", null ),
			"class attribute should be white listed"
	);
	assertFalse(
			getSafeHtmlValidator().isValid( "<div style='foo'>test</div>", null ),
			"style attribute is not white listed"
	);
}

public List<IndexEntry> getSubmodules( String treeIsh ) throws GitException, InterruptedException {
    List<IndexEntry> submodules = lsTree(treeIsh,true);

    // Remove anything that isn't a submodule
    for (Iterator<IndexEntry> it = submodules.iterator(); it.hasNext();) {
        if (!it.next().getMode().equals("160000")) {
            it.remove();
        }
    }
    return submodules;
}

public FetchCommand fetch_() {
    return new FetchCommand() {
        public URIish url;
        public List<RefSpec> refspecs;
        public boolean prune;
        public boolean shallow;
        public Integer timeout;
        public boolean tags = true;
        public Integer depth = 1;

        public FetchCommand from(URIish remote, List<RefSpec> refspecs) {
            this.url = remote;
            this.refspecs = refspecs;
            return this;
        }

        public FetchCommand tags(boolean tags) {
            this.tags = tags;
            return this;
        }

        public FetchCommand prune() {
            return prune(true);
        }

        public FetchCommand prune(boolean prune) {
            this.prune = prune;
            return this;
        }

        public FetchCommand shallow(boolean shallow) {
            this.shallow = shallow;
            return this;
        }

        public FetchCommand timeout(Integer timeout) {
        	this.timeout = timeout;
        	return this;
        }

        public FetchCommand depth(Integer depth) {
            this.depth = depth;
            return this;
        }

        public void execute() throws GitException, InterruptedException {
            listener.getLogger().println(
                    "Fetching upstream changes from " + url);

            ArgumentListBuilder args = new ArgumentListBuilder();
            args.add("fetch");
            args.add(tags ? "--tags" : "--no-tags");
            if (USE_FORCE_FETCH && isAtLeastVersion(2, 20, 0, 0)) {
                /* CLI git 2.20.0 fixed a long-standing bug that now requires --force to update existing tags */
                args.add("--force");
            }
            if (isAtLeastVersion(1,7,1,0))
                args.add("--progress");

            StandardCredentials cred = credentials.get(url.toPrivateString());
            if (cred == null) cred = defaultCredentials;
            args.add(url);

            if (refspecs != null)
                for (RefSpec rs: refspecs)
                    if (rs != null)
                        args.add(rs.toString());

            if (prune) args.add("--prune");

            if (shallow) {
                if (depth == null){
                    depth = 1;
                }
                args.add("--depth=" + depth);
            }

            warnIfWindowsTemporaryDirNameHasSpaces();

            /* If url looks like a remote name reference, convert to remote URL for authentication */
            /* See JENKINS-50573 for more details */
            /* "git remote add" rejects remote names with ':' (and it is a common character in remote URLs) */
            /* "git remote add" allows remote names with '@' but internal git parsing problems seem likely (and it is a common character in remote URLs) */
            /* "git remote add" allows remote names with '/' but git client plugin parsing problems will occur (and it is a common character in remote URLs) */
            /* "git remote add" allows remote names with '\' but git client plugin parsing problems will occur */
            URIish remoteUrl = url;
            if (!url.isRemote() && !StringUtils.containsAny(url.toString(), ":@/\\")) {
                try {
                    remoteUrl = new URIish(getRemoteUrl(url.toString()));
                } catch (URISyntaxException e) {
                    listener.getLogger().println("Unexpected remote name or URL: '" + url + "'");
                }
            }

            launchCommandWithCredentials(args, workspace, cred, remoteUrl, timeout);
        }
    };
}

public Map<String, ObjectId> getRemoteReferences(String url, String pattern, boolean headsOnly, boolean tagsOnly)
        throws GitException, InterruptedException {
    ArgumentListBuilder args = new ArgumentListBuilder("ls-remote");
    if (headsOnly) {
        args.add("-h");
    }
    if (tagsOnly) {
        args.add("-t");
    }
    args.add(url);
    if (pattern != null) {
        args.add(pattern);
    }

    StandardCredentials cred = credentials.get(url);
    if (cred == null) cred = defaultCredentials;

    String result = launchCommandWithCredentials(args, null, cred, url);

    Map<String, ObjectId> references = new HashMap<>();
    String[] lines = result.split("\n");
    for (String line : lines) {
        if (line.length() < 41) {
            continue; // throw new GitException("unexpected ls-remote output " + line);
        }
        String refName = line.substring(41);
        ObjectId refObjectId = ObjectId.fromString(line.substring(0, 40));
        if (refName.startsWith("refs/tags") && refName.endsWith("^{}")) {
            // get peeled object id for annotated tag
            String tagName = refName.replace("^{}", "");
            // Replace with the peeled object id if the entry with tagName exists
            references.put(tagName, refObjectId);
        } else {
            if (!references.containsKey(refName)) {
                references.put(refName, refObjectId);
            }
        }

    }
    return references;
}

