public void createPlainAccessConfig(final String addr, final PlainAccessConfig plainAccessConfig,
    final long timeoutMillis)
    throws RemotingException, MQBrokerException, InterruptedException, MQClientException {
    CreateAccessConfigRequestHeader requestHeader = new CreateAccessConfigRequestHeader();
    requestHeader.setAccessKey(plainAccessConfig.getAccessKey());
    requestHeader.setSecretKey(plainAccessConfig.getSecretKey());
    requestHeader.setAdmin(plainAccessConfig.isAdmin());
    requestHeader.setDefaultGroupPerm(plainAccessConfig.getDefaultGroupPerm());
    requestHeader.setDefaultTopicPerm(plainAccessConfig.getDefaultTopicPerm());
    requestHeader.setWhiteRemoteAddress(plainAccessConfig.getWhiteRemoteAddress());
    requestHeader.setTopicPerms(UtilAll.List2String(plainAccessConfig.getTopicPerms(), ","));
    requestHeader.setGroupPerms(UtilAll.List2String(plainAccessConfig.getGroupPerms(), ","));

    RemotingCommand request = RemotingCommand.createRequestCommand(RequestCode.UPDATE_AND_CREATE_ACL_CONFIG, requestHeader);

    RemotingCommand response = this.remotingClient.invokeSync(MixAll.brokerVIPChannel(this.clientConfig.isVipChannelEnabled(), addr),
        request, timeoutMillis);
    assert response != null;
    switch (response.getCode()) {
        case ResponseCode.SUCCESS: {
            return;
        }
        default:
            break;
    }

    throw new MQClientException(response.getCode(), response.getRemark());
}

public static List<String> String2List(String str, String splitor) {
    if (StringUtils.isEmpty(str)) {
        return null;
    }

    String[] addrArray = str.split(splitor);
    return Arrays.asList(addrArray);
}

public static String List2String(List<String> list, String splitor) {
    if (list == null || list.size() == 0) {
        return null;
    }
    StringBuffer str = new StringBuffer();
    for (int i = 0; i < list.size(); i++) {
        str.append(list.get(i));
        if (i == list.size() - 1) {
            continue;
        }
        str.append(splitor);
    }
    return str.toString();
}

private void writeRESTfulEndpoint(PrintWriter writer,
                                  String basePath,
                                  AbstractDestination sd) {
    String absoluteURL = getAbsoluteAddress(basePath, sd);
    if (absoluteURL == null) {
        return;
    }

    writer.write("<tr><td>");
    writer.write("<span class=\"field\">Endpoint address:</span> " + "<span class=\"value\">"
                 + absoluteURL + "</span>");

    Bus sb = bus;
    if (sd instanceof AbstractHTTPDestination) {
        sb = ((AbstractHTTPDestination)sd).getBus();
    }        

    addWadlIfNeeded(absoluteURL, sb, writer);
    addOpenApiIfNeeded(absoluteURL, sb, writer);
    addSwaggerIfNeeded(absoluteURL, sb, writer);
    addAtomLinkIfNeeded(absoluteURL, atomMap, writer);
    writer.write("</td></tr>");
}

private static void writeApiSpec(String absoluteURL, Bus sb, PrintWriter writer, 
        String specPath, String specName) {
    if (PropertyUtils.isTrue(sb.getProperty("swagger.service.ui.available"))) {
        URI uri = URI.create(absoluteURL);
        String schemePath = uri.getScheme() + "://" + uri.getHost()
            + (uri.getPort() == -1 ? "" : ":" + uri.getPort());
        String relPath = absoluteURL.substring(schemePath.length());
        if (!relPath.endsWith("/")) {
            relPath += "/";
        }
        specPath = "api-docs?url=" + relPath + specPath;
    }
    if (!absoluteURL.endsWith("/")) {
        specPath = "/" + specPath;
    }
    writer.write("<br/><span class=\"field\">" + specName + " :</span> " + "<a href=\"" + absoluteURL
             + specPath + "\">" + absoluteURL + specPath + "</a>");
}

private static String getExtensionEndpointAddress(String endpointAddress, Map<String, String> extMap) {
    if (extMap != null) {
        for (Map.Entry<String, String> entry : extMap.entrySet()) {
            if (endpointAddress.endsWith(entry.getKey())) {
                endpointAddress = endpointAddress.substring(0, endpointAddress.length()
                                                               - entry.getKey().length());
                endpointAddress += entry.getValue();
                return endpointAddress;
            }
        }
    }
    return null;
}

private void writerSoapEndpoint(PrintWriter writer,
                                String basePath,
                                AbstractDestination sd) {
    String absoluteURL = getAbsoluteAddress(basePath, sd);
    if (absoluteURL == null) {
        return;
    }

    writer.write("<tr><td>");
    writer.write("<span class=\"porttypename\">"
                 + sd.getEndpointInfo().getInterface().getName().getLocalPart() + "</span>");
    writer.write("<ul>");
    for (OperationInfo oi : sd.getEndpointInfo().getInterface().getOperations()) {
        if (!Boolean.TRUE.equals(oi.getProperty("operation.is.synthetic"))) {
            writer.write("<li>" + oi.getName().getLocalPart() + "</li>");
        }
    }
    writer.write("</ul>");
    writer.write("</td><td>");


    writer.write("<span class=\"field\">Endpoint address:</span> " + "<span class=\"value\">"
                 + absoluteURL + "</span>");
    writer.write("<br/><span class=\"field\">WSDL :</span> " + "<a href=\"" + absoluteURL
                 + "?wsdl\">" + sd.getEndpointInfo().getService().getName() + "</a>");
    writer.write("<br/><span class=\"field\">Target namespace:</span> "
                 + "<span class=\"value\">"
                 + sd.getEndpointInfo().getService().getTargetNamespace() + "</span>");
    addAtomLinkIfNeeded(absoluteURL, atomMap, writer);
    writer.write("</td></tr>");
}

private void writeUnformattedSOAPEndpoints(PrintWriter writer,
                                           String baseAddress,
                                           AbstractDestination[] destinations) throws IOException {
    for (AbstractDestination sd : destinations) {
        String address = getAbsoluteAddress(baseAddress, sd);
        writer.write(address);

        if (renderWsdlList) {
            writer.write("?wsdl");
        }
        writer.write('\n');
    }
    writer.write('\n');
}

private void writeUnformattedRESTfulEndpoints(PrintWriter writer,
                                              String baseAddress,
                                              AbstractDestination[] destinations) throws IOException {
    for (AbstractDestination sd : destinations) {
        String address = getAbsoluteAddress(baseAddress, sd);
        boolean wadlAvailable = bus != null
            && PropertyUtils.isTrue(bus.getProperty("wadl.service.descrition.available"));
        boolean swaggerAvailable = bus != null
            && PropertyUtils.isTrue(bus.getProperty("swagger.service.descrition.available"));
        boolean openApiAvailable = bus != null
            && PropertyUtils.isTrue(bus.getProperty("openapi.service.descrition.available"));
        if (!wadlAvailable && !swaggerAvailable) {
            writer.write(address + "\n");
            return;
        }
        if (wadlAvailable) {
            writer.write(address + "?_wadl\n");
        }
        if (swaggerAvailable) {
            writer.write(address + "/swagger.json\n");
        }
        if (openApiAvailable) {
            writer.write(address + "/openapi.json\n");
        }
    }
}

public void handle(RoutingContext context) {
  HttpServerRequest request = context.request();
  if (request.method() != HttpMethod.GET && request.method() != HttpMethod.HEAD) {
    if (log.isTraceEnabled()) log.trace("Not GET or HEAD so ignoring request");
    context.next();
  } else {
    String path = HttpUtils.removeDots(URIDecoder.decodeURIComponent(context.normalisedPath(), false));
    // if the normalized path is null it cannot be resolved
    if (path == null) {
      log.warn("Invalid path: " + context.request().path());
      context.next();
      return;
    }

    // only root is known for sure to be a directory. all other directories must be identified as such.
    if (!directoryListing && "/".equals(path)) {
      path = indexPage;
    }

    // can be called recursive for index pages
    sendStatic(context, path);
  }
}

