private boolean isInvalidEncodedPath(String path) {
	if (path.contains("%")) {
		try {
			// Use URLDecoder (vs UriUtils) to preserve potentially decoded UTF-8
			// chars
			String decodedPath = URLDecoder.decode(path, "UTF-8");
			if (isInvalidPath(decodedPath)) {
				return true;
			}
			decodedPath = processPath(decodedPath);
			if (isInvalidPath(decodedPath)) {
				return true;
			}
		}
		catch (IllegalArgumentException | UnsupportedEncodingException ex) {
			// Should never happen...
		}
	}
	return false;
}

public synchronized Resource findOne(String application, String profile, String label,
		String path) {

	if (StringUtils.hasText(path)) {
		String[] locations = this.service.getLocations(application, profile, label)
				.getLocations();
		try {
			for (int i = locations.length; i-- > 0;) {
				String location = locations[i];
				for (String local : getProfilePaths(profile, path)) {
					if (!isInvalidPath(local) && !isInvalidEncodedPath(local)) {
						Resource file = this.resourceLoader.getResource(location)
								.createRelative(local);
						if (file.exists() && file.isReadable()) {
							return file;
						}
					}
				}
			}
		}
		catch (IOException e) {
			throw new NoSuchResourceException(
					"Error : " + path + ". (" + e.getMessage() + ")");
		}
	}
	throw new NoSuchResourceException("Not found: " + path);
}

private Collection<String> getProfilePaths(String profiles, String path) {
	Set<String> paths = new LinkedHashSet<>();
	for (String profile : StringUtils.commaDelimitedListToSet(profiles)) {
		if (!StringUtils.hasText(profile) || "default".equals(profile)) {
			paths.add(path);
		}
		else {
			String ext = StringUtils.getFilenameExtension(path);
			String file = path;
			if (ext != null) {
				ext = "." + ext;
				file = StringUtils.stripFilenameExtension(path);
			}
			else {
				ext = "";
			}
			paths.add(file + "-" + profile + ext);
		}
	}
	paths.add(path);
	return paths;
}

public byte[] binary(@PathVariable String name, @PathVariable String profile,
		@PathVariable String label, ServletWebRequest request) throws IOException {
	String path = getFilePath(request, name, profile, label);
	return binary(request, name, profile, label, path);
}

private String resolveName(String name) {
	if (name != null && name.contains("(_)")) {
		// "(_)" is uncommon in a git repo name, but "/" cannot be matched
		// by Spring MVC
		name = name.replace("(_)", "/");
	}
	return name;
}

public String retrieve(@PathVariable String name, @PathVariable String profile,
		@PathVariable String label, ServletWebRequest request,
		@RequestParam(defaultValue = "true") boolean resolvePlaceholders)
		throws IOException {
	String path = getFilePath(request, name, profile, label);
	return retrieve(request, name, profile, label, path, resolvePlaceholders);
}

private boolean checkNotModified(ServletWebRequest request, Resource resource) {
	try {
		return request != null && request.checkNotModified(resource.lastModified());
	}
	catch (Exception ex) {
		// Ignore the exception since caching is optional.
	}
	return false;
}

public synchronized Resource findOne(String application, String profile, String label,
		String path) {

	if (StringUtils.hasText(path)) {
		String[] locations = this.service.getLocations(application, profile, label)
				.getLocations();
		try {
			for (int i = locations.length; i-- > 0;) {
				String location = locations[i];
				if (PathUtils.isInvalidEncodedLocation(location)) {
					continue;
				}
				for (String local : getProfilePaths(profile, path)) {
					if (!PathUtils.isInvalidPath(local)
							&& !PathUtils.isInvalidEncodedPath(local)) {
						Resource file = this.resourceLoader.getResource(location)
								.createRelative(local);
						if (file.exists() && file.isReadable()) {
							return file;
						}
					}
				}
			}
		}
		catch (IOException e) {
			throw new NoSuchResourceException(
					"Error : " + path + ". (" + e.getMessage() + ")");
		}
	}
	throw new NoSuchResourceException("Not found: " + path);
}

