public Result<Object> createDirectory(User loginUser,
                                      String name,
                                      String description,
                                      ResourceType type,
                                      int pid,
                                      String currentDir) {
    Result<Object> result = checkResourceUploadStartupState();
    if (!result.getCode().equals(Status.SUCCESS.getCode())) {
        return result;
    }
    if (FileUtils.directoryTraversal(name)) {
        putMsg(result, Status.VERIFY_PARAMETER_NAME_FAILED);
        return result;
    }
    String fullName = currentDir.equals("/") ? String.format("%s%s",currentDir,name) : String.format("%s/%s",currentDir,name);
    result = verifyResource(loginUser, type, fullName, pid);
    if (!result.getCode().equals(Status.SUCCESS.getCode())) {
        return result;
    }

    if (checkResourceExists(fullName, 0, type.ordinal())) {
        logger.error("resource directory {} has exist, can't recreate", fullName);
        putMsg(result, Status.RESOURCE_EXIST);
        return result;
    }

    Date now = new Date();

    Resource resource = new Resource(pid,name,fullName,true,description,name,loginUser.getId(),type,0,now,now);

    try {
        resourcesMapper.insert(resource);
        putMsg(result, Status.SUCCESS);
        Map<Object, Object> dataMap = new BeanMap(resource);
        Map<String, Object> resultMap = new HashMap<>();
        for (Map.Entry<Object, Object> entry: dataMap.entrySet()) {
            if (!"class".equalsIgnoreCase(entry.getKey().toString())) {
                resultMap.put(entry.getKey().toString(), entry.getValue());
            }
        }
        result.setData(resultMap);
    } catch (DuplicateKeyException e) {
        logger.error("resource directory {} has exist, can't recreate", fullName);
        putMsg(result, Status.RESOURCE_EXIST);
        return result;
    } catch (Exception e) {
        logger.error("resource already exists, can't recreate ", e);
        throw new ServiceException("resource already exists, can't recreate");
    }
    //create directory in hdfs
    createDirectory(loginUser,fullName,type,result);
    return result;
}

private Result<Object> verifyFile(String name, ResourceType type, MultipartFile file) {
    Result<Object> result = new Result<>();
    putMsg(result, Status.SUCCESS);

    if (FileUtils.directoryTraversal(name)) {
        logger.error("file alias name {} verify failed", name);
        putMsg(result, Status.VERIFY_PARAMETER_NAME_FAILED);
        return result;
    }

    if (file != null && FileUtils.directoryTraversal(Objects.requireNonNull(file.getOriginalFilename()))) {
        logger.error("file original name {} verify failed", file.getOriginalFilename());
        putMsg(result, Status.VERIFY_PARAMETER_NAME_FAILED);
        return result;
    }

    if (file != null) {
        // file is empty
        if (file.isEmpty()) {
            logger.error("file is empty: {}", RegexUtils.escapeNRT(file.getOriginalFilename()));
            putMsg(result, Status.RESOURCE_FILE_IS_EMPTY);
            return result;
        }

        // file suffix
        String fileSuffix = Files.getFileExtension(file.getOriginalFilename());
        String nameSuffix = Files.getFileExtension(name);

        // determine file suffix
        if (!(StringUtils.isNotEmpty(fileSuffix) && fileSuffix.equalsIgnoreCase(nameSuffix))) {
            // rename file suffix and original suffix must be consistent
            logger.error("rename file suffix and original suffix must be consistent: {}", RegexUtils.escapeNRT(file.getOriginalFilename()));
            putMsg(result, Status.RESOURCE_SUFFIX_FORBID_CHANGE);
            return result;
        }

        //If resource type is UDF, only jar packages are allowed to be uploaded, and the suffix must be .jar
        if (Constants.UDF.equals(type.name()) && !JAR.equalsIgnoreCase(fileSuffix)) {
            logger.error(Status.UDF_RESOURCE_SUFFIX_NOT_JAR.getMsg());
            putMsg(result, Status.UDF_RESOURCE_SUFFIX_NOT_JAR);
            return result;
        }
        if (file.getSize() > Constants.MAX_FILE_SIZE) {
            logger.error("file size is too large: {}", RegexUtils.escapeNRT(file.getOriginalFilename()));
            putMsg(result, Status.RESOURCE_SIZE_EXCEED_LIMIT);
            return result;
        }
    }
    return result;
}

public Result<Object> onlineCreateResource(User loginUser, ResourceType type, String fileName, String fileSuffix, String desc, String content,int pid,String currentDir) {
    Result<Object> result = checkResourceUploadStartupState();
    if (!result.getCode().equals(Status.SUCCESS.getCode())) {
        return result;
    }
    if (FileUtils.directoryTraversal(fileName)) {
        putMsg(result, Status.VERIFY_PARAMETER_NAME_FAILED);
        return result;
    }

    //check file suffix
    String nameSuffix = fileSuffix.trim();
    String resourceViewSuffixs = FileUtils.getResourceViewSuffixs();
    if (StringUtils.isNotEmpty(resourceViewSuffixs)) {
        List<String> strList = Arrays.asList(resourceViewSuffixs.split(","));
        if (!strList.contains(nameSuffix)) {
            logger.error("resource suffix {} not support create", nameSuffix);
            putMsg(result, Status.RESOURCE_SUFFIX_NOT_SUPPORT_VIEW);
            return result;
        }
    }

    String name = fileName.trim() + "." + nameSuffix;
    String fullName = currentDir.equals("/") ? String.format("%s%s",currentDir,name) : String.format("%s/%s",currentDir,name);
    result = verifyResource(loginUser, type, fullName, pid);
    if (!result.getCode().equals(Status.SUCCESS.getCode())) {
        return result;
    }

    // save data
    Date now = new Date();
    Resource resource = new Resource(pid,name,fullName,false,desc,name,loginUser.getId(),type,content.getBytes().length,now,now);

    resourcesMapper.insert(resource);
    updateParentResourceSize(resource, resource.getSize());

    putMsg(result, Status.SUCCESS);
    Map<Object, Object> dataMap = new BeanMap(resource);
    Map<String, Object> resultMap = new HashMap<>();
    for (Map.Entry<Object, Object> entry: dataMap.entrySet()) {
        if (!Constants.CLASS.equalsIgnoreCase(entry.getKey().toString())) {
            resultMap.put(entry.getKey().toString(), entry.getValue());
        }
    }
    result.setData(resultMap);

    String tenantCode = tenantMapper.queryById(loginUser.getTenantId()).getTenantCode();

    result = uploadContentToHdfs(fullName, tenantCode, content);
    if (!result.getCode().equals(Status.SUCCESS.getCode())) {
        throw new ServiceException(result.getMsg());
    }
    return result;
}

private boolean isFileWithinDirectory(
        final File dir,
        final File file
) throws IOException {
    final File dir_ = dir.getAbsoluteFile();
    if (dir_.isDirectory()) {
        final File fl = new File(dir_, file.getPath());
        if (fl.isFile()) {
            if (fl.getCanonicalPath().startsWith(dir_.getCanonicalPath())) {
                // Prevent accessing files outside the load-path.
                // E.g.: ../../coffee
                return true;
            }
        }
    }

    return false;
}

public static boolean normalize(MessageBytes uriMB) {

    int type = uriMB.getType();
    if (type == MessageBytes.T_CHARS) {
        return normalizeChars(uriMB);
    } else {
        return normalizeBytes(uriMB);
    }
}

    protected boolean postParseRequest(final org.glassfish.grizzly.http.server.Request req,
                                       final Request request,
                                       final org.glassfish.grizzly.http.server.Response res,
                                       final Response response,
                                       final boolean v3Enabled)
        throws Exception {
        // XXX the processor may have set a correct scheme and port prior to this point, 
        // in ajp13 protocols dont make sense to get the port from the connector...
        // otherwise, use connector configuration
        request.setSecure(req.isSecure());

        // URI decoding
        DataChunk decodedURI;
        try {
            decodedURI = req.getRequest().getRequestURIRef().getDecodedRequestURIBC();
        } catch (CharConversionException cce) {
            response.sendError(HttpServletResponse.SC_BAD_REQUEST, "Invalid URI");
            return false;
        }

        if (compatWithTomcat || !v3Enabled) {
//            decodedURI.duplicate(req.requestURI());
//            try {
//              req.getURLDecoder().convert(decodedURI, false);
//            } catch (IOException ioe) {
//              res.setStatus(400);
//              res.setMessage("Invalid URI: " + ioe.getMessage());
//              return false;
//            }

            /* GlassFish Issue 2339
            // Normalize decoded URI
            if (!normalize(req.decodedURI())) {
                res.setStatus(400);
                res.setMessage("Invalid URI");
                return false;
            }
            */

            // Set the remote principal
            String principal = req.getRemoteUser();
            if (principal != null) {
                request.setUserPrincipal(new CoyotePrincipal(principal));
            }

            // Set the authorization type
            String authtype = req.getAuthType();
            if (authtype != null) {
                request.setAuthType(authtype);
            }

            /* CR 6309511
            // URI character decoding
            convertURI(decodedURI, request);

            // Parse session Id
            parseSessionId(req, request);
             */
            // START CR 6309511
//             URI character decoding
//            request.convertURI(decodedURI);

            // START GlassFish Issue 2339
            // Normalize decoded URI
//            if (!normalize(decodedURI)) {
//                res.setStatus(400);
//                res.setMessage("Invalid URI");
//                return false;
//            }
            // END GlassFish Issue 2339
        }
        // END CR 6309511

        /*
         * Remove any parameters from the URI, so they won't be considered
         * by the mapping algorithm, and save them in a temporary CharChunk,
         * so that any session id param may be parsed once the target
         * context, which may use a custom session parameter name, has been
         * identified
         */
        final CharChunk uriParamsCC = request.getURIParams();
        final CharChunk uriCC = decodedURI.getCharChunk();
        final int semicolon = uriCC.indexOf(';');
        if (semicolon > 0) {
            final int absSemicolon = uriCC.getStart() + semicolon;
            uriParamsCC.setChars(uriCC.getBuffer(), absSemicolon,
                uriCC.getEnd() - absSemicolon);
            decodedURI.setChars(uriCC.getBuffer(), uriCC.getStart(),
                absSemicolon - uriCC.getStart());
        }

        if (compatWithTomcat || !v3Enabled) {
            /*mod_jk*/
            DataChunk localDecodedURI = decodedURI;
            if (semicolon > 0) {
                localDecodedURI = req.getNote(DATA_CHUNK);
                if (localDecodedURI == null) {
                    localDecodedURI = DataChunk.newInstance();
                    req.setNote(DATA_CHUNK, localDecodedURI);
                }
                localDecodedURI.duplicate(decodedURI);
            }
            connector.getMapper().map(req.getRequest().serverName(), localDecodedURI,
                                  request.getMappingData());
            MappingData md = request.getMappingData();
            req.setNote(MAPPING_DATA, md);
            request.updatePaths(md);
        }

        // FIXME: the code below doesnt belongs to here,
        // this is only have sense
        // in Http11, not in ajp13..
        // At this point the Host header has been processed.
        // Override if the proxyPort/proxyHost are set
        String proxyName = connector.getProxyName();
        int proxyPort = connector.getProxyPort();
        if (proxyPort != 0) {
            req.setServerPort(proxyPort);
        }
        if (proxyName != null) {
            req.setServerName(proxyName);
        }

        Context ctx = (Context) request.getMappingData().context;

        // Parse session id
        if (ctx != null) {
            if (req.isRequestedSessionIdFromURL() &&
                    Globals.SESSION_PARAMETER_NAME.equals(ctx.getSessionParameterName())) {
                request.obtainSessionId();
            } else if (!uriParamsCC.isNull()) {
//            String sessionParam = ";" + ctx.getSessionParameterName() + "=";
                request.parseSessionId(ctx.getSessionParameterName(), uriParamsCC);
            }
        }

        // START GlassFish 1024
        request.setDefaultContext(request.getMappingData().isDefaultContext);
        // END GlassFish 1024

        // START SJSAS 6253524
        // request.setContext((Context) request.getMappingData().context);
        // END SJSAS 6253524
        // START SJSAS 6253524
        request.setContext(ctx);
        // END SJSAS 6253524

        if (ctx != null && !uriParamsCC.isNull()) {
            request.parseSessionVersion(uriParamsCC);
        }

        if (!uriParamsCC.isNull()) {
            request.parseJReplica(uriParamsCC);
        }

        request.setWrapper((Wrapper) request.getMappingData().wrapper);

        // Filter trace method
        if (!connector.getAllowTrace() && Method.TRACE.equals(req.getMethod())) {
            Wrapper wrapper = request.getWrapper();
            String header = null;
            if (wrapper != null) {
                String[] methods = wrapper.getServletMethods();
                if (methods != null) {
                    for (String method : methods) {
                        // Exclude TRACE from methods returned in Allow header
                        if ("TRACE".equals(method)) {
                            continue;
                        }
                        if (header == null) {
                            header = method;
                        } else {
                            header += ", " + method;
                        }
                    }
                }
            }                               
            res.setStatus(405, "TRACE method is not allowed");
            res.addHeader("Allow", header);
            return false;
        }

        // Possible redirect
        DataChunk redirectPathMB = request.getMappingData().redirectPath;
        // START SJSAS 6253524
        // if (!redirectPathMB.isNull()) {
        // END SJSAS 6253524
        // START SJSAS 6253524
        if (!redirectPathMB.isNull()
            && (!ctx.hasAdHocPaths()
                || (ctx.getAdHocServletName(((HttpServletRequest)
                        request.getRequest()).getServletPath()) == null))) {
        // END SJSAS 6253524
            String redirectPath = redirectPathMB.toString();
            String query = request.getQueryString();
            if (request.isRequestedSessionIdFromURL()) {
                // This is not optimal, but as this is not very common, it
                // shouldn't matter
                redirectPath = redirectPath + ";" + ctx.getSessionParameterName() + "=" 
                    + request.getRequestedSessionId();
            }            
            // START GlassFish 936
            redirectPath = response.encode(redirectPath);
            // END GlassFish 936
            if (query != null) {
                // This is not optimal, but as this is not very common, it
                // shouldn't matter
                redirectPath = redirectPath + "?" + query;
            }

            // START CR 6590921
            boolean authPassthroughEnabled = 
                connector.getAuthPassthroughEnabled();
            ProxyHandler proxyHandler = connector.getProxyHandler();
            if (authPassthroughEnabled && proxyHandler != null) {

                if (proxyHandler.getSSLKeysize(
                        (HttpServletRequest)request.getRequest()) > 0) {
                    request.setSecure(true);
                }
            }
            // END CR 6590921
            // Issue a permanent redirect
            response.sendRedirect(redirectPath, false);

            return false;
        }

        // Parse session Id
        /* CR 6309511
        parseSessionCookiesId(req, request);
         */
        // START CR 6309511
        request.parseSessionCookiesId();
        // END CR 6309511

        // START SJSAS 6346226
        request.parseJrouteCookie();
        // END SJSAS 6346226

        return true;
    }


    /**
     * Normalize URI.
     * <p>
     * This method normalizes "\", "//", "/./" and "/../". This method will
     * return false when trying to go above the root, or if the URI contains
     * a null byte.
     * 
     * @param uriMB URI to be normalized
     */
    public static boolean normalize(MessageBytes uriMB) {

        int type = uriMB.getType();
        if (type == MessageBytes.T_CHARS) {
            return normalizeChars(uriMB);
        } else {
            return normalizeBytes(uriMB);
        }
    }


    private static boolean normalizeBytes(MessageBytes uriMB) {

        ByteChunk uriBC = uriMB.getByteChunk();
        byte[] b = uriBC.getBytes();
        int start = uriBC.getStart();
        int end = uriBC.getEnd();

        // An empty URL is not acceptable
        if (start == end)
            return false;

        // URL * is acceptable
