            || ((b[end - 2] == (byte) '.') 
                && (b[end - 3] == (byte) '/'))) {
            b[end] = (byte) '/';
            end++;
        }
    }

    uriBC.setEnd(end);

    index = 0;

    // Resolve occurrences of "/./" in the normalized path
    while (true) {
        index = uriBC.indexOf("/./", 0, 3, index);
        if (index < 0)
            break;
        copyBytes(b, start + index, start + index + 2, 
                  end - start - index - 2);
        end = end - 2;
        uriBC.setEnd(end);
    }

    index = 0;

    // Resolve occurrences of "/../" in the normalized path
    while (true) {
        index = uriBC.indexOf("/../", 0, 4, index);
        if (index < 0)
            break;
