public void refreshTokenReuseTokenWithRefreshTokensRevokedAfterSingleReuse() throws Exception {
    try {
        RealmManager.realm(adminClient.realm("test"))
                .revokeRefreshToken(true)
                .refreshTokenMaxReuse(1);

        oauth.doLogin("test-user@localhost", "password");

        EventRepresentation loginEvent = events.expectLogin().assertEvent();

        String sessionId = loginEvent.getSessionId();
        String codeId = loginEvent.getDetails().get(Details.CODE_ID);

        String code = oauth.getCurrentQuery().get(OAuth2Constants.CODE);

        OAuthClient.AccessTokenResponse initialResponse = oauth.doAccessTokenRequest(code, "password");
        RefreshToken initialRefreshToken = oauth.parseRefreshToken(initialResponse.getRefreshToken());

        events.expectCodeToToken(codeId, sessionId).assertEvent();

        setTimeOffset(2);

        // Initial refresh.
        OAuthClient.AccessTokenResponse responseFirstUse = oauth.doRefreshTokenRequest(initialResponse.getRefreshToken(), "password");
        RefreshToken newTokenFirstUse = oauth.parseRefreshToken(responseFirstUse.getRefreshToken());

        assertEquals(200, responseFirstUse.getStatusCode());

        events.expectRefresh(initialRefreshToken.getId(), sessionId).assertEvent();

        setTimeOffset(4);

        // Second refresh (allowed).
        OAuthClient.AccessTokenResponse responseFirstReuse = oauth.doRefreshTokenRequest(initialResponse.getRefreshToken(), "password");
        RefreshToken newTokenFirstReuse = oauth.parseRefreshToken(responseFirstReuse.getRefreshToken());

        assertEquals(200, responseFirstReuse.getStatusCode());

        events.expectRefresh(initialRefreshToken.getId(), sessionId).assertEvent();

        setTimeOffset(6);
        // Token reused twice, became invalid.
        OAuthClient.AccessTokenResponse responseSecondReuse = oauth.doRefreshTokenRequest(initialResponse.getRefreshToken(), "password");

        assertEquals(400, responseSecondReuse.getStatusCode());

        events.expectRefresh(initialRefreshToken.getId(), sessionId).removeDetail(Details.TOKEN_ID)
                .removeDetail(Details.UPDATED_REFRESH_TOKEN_ID).error("invalid_token").assertEvent();

        setTimeOffset(8);
        // Refresh token from first use became invalid.
        OAuthClient.AccessTokenResponse responseUseOfInvalidatedRefreshToken =
                oauth.doRefreshTokenRequest(responseFirstUse.getRefreshToken(), "password");

        assertEquals(400, responseUseOfInvalidatedRefreshToken.getStatusCode());

        events.expectRefresh(newTokenFirstUse.getId(), sessionId).removeDetail(Details.TOKEN_ID)
                .removeDetail(Details.UPDATED_REFRESH_TOKEN_ID).error("invalid_token").assertEvent();

        setTimeOffset(10);
        // Refresh token from reuse is not valid. Client session was invalidated
        OAuthClient.AccessTokenResponse responseUseOfValidRefreshToken =
                oauth.doRefreshTokenRequest(responseFirstReuse.getRefreshToken(), "password");

        assertEquals(400, responseUseOfValidRefreshToken.getStatusCode());

        events.expectRefresh(newTokenFirstReuse.getId(), sessionId).removeDetail(Details.TOKEN_ID)
                .removeDetail(Details.UPDATED_REFRESH_TOKEN_ID).error("invalid_token").assertEvent();
    } finally {
        setTimeOffset(0);
        RealmManager.realm(adminClient.realm("test"))
                .refreshTokenMaxReuse(0)
                .revokeRefreshToken(false);
    }
}

private boolean testngXmlExistsInJar(File jarFile, List<String> classes) throws IOException {
  try (JarFile jf = new JarFile(jarFile)) {
    Enumeration<JarEntry> entries = jf.entries();
    File file = java.nio.file.Files.createTempDirectory("testngXmlPathInJar-").toFile();
    String suitePath = null;
    while (entries.hasMoreElements()) {
      JarEntry je = entries.nextElement();
      String jeName = je.getName();
      if (Parser.canParse(jeName.toLowerCase())) {
        InputStream inputStream = jf.getInputStream(je);
        File copyFile = new File(file, jeName);
        Files.copyFile(inputStream, copyFile);
        if (matchesXmlPathInJar(je)) {
          suitePath = copyFile.toString();
        }
      } else if (isJavaClass(je)) {
        classes.add(constructClassName(je));
      }
    }
    if (Strings.isNullOrEmpty(suitePath)) {
      return false;
    }
    Collection<XmlSuite> parsedSuites = Parser.parse(suitePath, processor);
    delete(file);
    for (XmlSuite suite : parsedSuites) {
      // If test names were specified, only run these test names
      if (testNames != null) {
        TestNamesMatcher testNamesMatcher = new TestNamesMatcher(suite, testNames);
        List<String> missMatchedTestname = testNamesMatcher.getMissMatchedTestNames();
        if (!missMatchedTestname.isEmpty()) {
          throw new TestNGException("The test(s) <" + missMatchedTestname + "> cannot be found.");
        }
        suites.addAll(testNamesMatcher.getSuitesMatchingTestNames());
      } else {
        suites.add(suite);
      }
      return true;
    }
  }
  return false;
}

public Response createResponse(UserModel.RequiredAction action) {

    String actionMessage;
    LoginFormsPages page;

    switch (action) {
        case CONFIGURE_TOTP:
            actionMessage = Messages.CONFIGURE_TOTP;
            page = LoginFormsPages.LOGIN_CONFIG_TOTP;
            break;
        case CONFIGURE_RECOVERY_AUTHN_CODES:
            actionMessage = Messages.CONFIGURE_BACKUP_CODES;
            page = LoginFormsPages.LOGIN_RECOVERY_AUTHN_CODES_CONFIG;
            break;
        case UPDATE_PROFILE:
            this.attributes.put(UPDATE_PROFILE_CONTEXT_ATTR, new UserUpdateProfileContext(realm, user));

            actionMessage = Messages.UPDATE_PROFILE;
            if(isDynamicUserProfile()) {
                page = LoginFormsPages.UPDATE_USER_PROFILE;
            } else {
                page = LoginFormsPages.LOGIN_UPDATE_PROFILE;
            }
            break;
        case UPDATE_EMAIL:
            actionMessage = Messages.UPDATE_EMAIL;
            page = LoginFormsPages.UPDATE_EMAIL;
            break;
        case UPDATE_PASSWORD:
            boolean isRequestedByAdmin = user.getRequiredActionsStream().filter(Objects::nonNull).anyMatch(UPDATE_PASSWORD.toString()::contains);
            actionMessage = isRequestedByAdmin ? Messages.UPDATE_PASSWORD : Messages.RESET_PASSWORD;
            page = LoginFormsPages.LOGIN_UPDATE_PASSWORD;
            break;
        case VERIFY_EMAIL:
            UpdateProfileContext userBasedContext1 = new UserUpdateProfileContext(realm,user);
            attributes.put("user",new ProfileBean(userBasedContext1,formData));
            actionMessage = Messages.VERIFY_EMAIL;
            page = LoginFormsPages.LOGIN_VERIFY_EMAIL;
            break;
        case VERIFY_PROFILE:
            UpdateProfileContext verifyProfile = new UserUpdateProfileContext(realm, user);
            this.attributes.put(UPDATE_PROFILE_CONTEXT_ATTR, verifyProfile);

            actionMessage = Messages.UPDATE_PROFILE;
            page = LoginFormsPages.UPDATE_USER_PROFILE;
            break;
        default:
            return Response.serverError().build();
    }

    if (messages == null) {
        setMessage(MessageType.WARNING, actionMessage);
    }

    return createResponse(page);
}

public Object exec(List list) throws TemplateModelException {
