private static String decodeRedirectUri(String redirectUri) {
    if (redirectUri == null) return null;
    int MAX_DECODING_COUNT = 5; // Max count of attempts for decoding URL (in case it was encoded multiple times)

    try {
        KeycloakUriBuilder uriBuilder = KeycloakUriBuilder.fromUri(redirectUri).preserveDefaultPort();
        String origQuery = uriBuilder.getQuery();
        String origFragment = uriBuilder.getFragment();
        String encodedRedirectUri = uriBuilder
                .replaceQuery(null)
                .fragment(null)
                .buildAsString();
        String decodedRedirectUri = null;

        for (int i = 0; i < MAX_DECODING_COUNT; i++) {
            decodedRedirectUri = Encode.decode(encodedRedirectUri);
            if (decodedRedirectUri.equals(encodedRedirectUri)) {
                // URL is decoded. We can return it (after attach original query and fragment)
                return KeycloakUriBuilder.fromUri(decodedRedirectUri).preserveDefaultPort()
                        .replaceQuery(origQuery)
                        .fragment(origFragment)
                        .buildAsString();
            } else {
                // Next attempt
                encodedRedirectUri = decodedRedirectUri;
            }
        }
    } catch (IllegalArgumentException iae) {
        logger.debugf("Illegal redirect URI used: %s, Details: %s", redirectUri, iae.getMessage());
    }
    logger.debugf("Was not able to decode redirect URI: %s", redirectUri);
    return null;
}

public static String validateRedirectUriWildcard(String redirectUri) {
    if (redirectUri == null)
        return null;

    int idx = redirectUri.indexOf("/*");
    if (idx > -1) {
        redirectUri = redirectUri.substring(0, idx);
    }
    return redirectUri;
}

private static boolean matchesRedirects(Set<String> validRedirects, String redirect, boolean allowWildcards) {
    logger.tracef("matchesRedirects: redirect URL to check: %s, allow wildcards: %b, Configured valid redirect URLs: %s", redirect, allowWildcards, validRedirects);
    for (String validRedirect : validRedirects) {
        if (validRedirect.endsWith("*") && !validRedirect.contains("?") && allowWildcards) {
            // strip off the query component - we don't check them when wildcards are effective
            String r = redirect.contains("?") ? redirect.substring(0, redirect.indexOf("?")) : redirect;
            // strip off *
            int length = validRedirect.length() - 1;
            validRedirect = validRedirect.substring(0, length);
            if (r.startsWith(validRedirect)) return true;
            // strip off trailing '/'
            if (length - 1 > 0 && validRedirect.charAt(length - 1) == '/') length--;
            validRedirect = validRedirect.substring(0, length);
            if (validRedirect.equals(r)) return true;
        } else if (validRedirect.equals(redirect)) return true;
    }
    return false;
}

public static String verifyRedirectUri(KeycloakSession session, String redirectUri, ClientModel client) {
    return verifyRedirectUri(session, redirectUri, client, true);
}

public static Set<String> resolveValidRedirects(KeycloakSession session, String rootUrl, Set<String> validRedirects) {
    // If the valid redirect URI is relative (no scheme, host, port) then use the request's scheme, host, and port
    Set<String> resolveValidRedirects = new HashSet<>();
    for (String validRedirect : validRedirects) {
        if (validRedirect.startsWith("/")) {
            validRedirect = relativeToAbsoluteURI(session, rootUrl, validRedirect);
            logger.debugv("replacing relative valid redirect with: {0}", validRedirect);
            resolveValidRedirects.add(validRedirect);
        } else {
            resolveValidRedirects.add(validRedirect);
        }
    }
    return resolveValidRedirects;
}

private static String getNormalizedRedirectUri(String redirectUri) {
    if (redirectUri != null) {
        try {
            URI uri = URI.create(redirectUri);
            redirectUri = uri.normalize().toString();
        } catch (IllegalArgumentException cause) {
            logger.debug("Invalid redirect uri", cause);
            return null;
        } catch (Exception cause) {
            logger.debug("Unexpected error when parsing redirect uri", cause);
            return null;
        }
        redirectUri = lowerCaseHostname(redirectUri);
    }
    return redirectUri;
}

public void authorizationRequestFragmentResponseModeNotKept() throws Exception {
    // Set response_mode=fragment and login
    oauth.responseMode(OIDCResponseMode.FRAGMENT.value());
    OAuthClient.AuthorizationEndpointResponse response = oauth.doLogin("test-user@localhost", "password");

    Assert.assertNotNull(response.getCode());
    Assert.assertNotNull(response.getState());

    URI currentUri = new URI(driver.getCurrentUrl());
    Assert.assertNull(currentUri.getRawQuery());
    Assert.assertNotNull(currentUri.getRawFragment());

    // Unset response_mode. The initial OIDC AuthenticationRequest won't contain "response_mode" parameter now and hence it should fallback to "query".
    oauth.responseMode(null);
    oauth.openLoginForm();
    response = new OAuthClient.AuthorizationEndpointResponse(oauth);

    Assert.assertNotNull(response.getCode());
    Assert.assertNotNull(response.getState());

    currentUri = new URI(driver.getCurrentUrl());
    Assert.assertNotNull(currentUri.getRawQuery());
    Assert.assertNull(currentUri.getRawFragment());
}

private void parseFeatures(FF4jConfiguration ff4jConfig, Map<String, String> mapConf) {
    int idx = 0;
    String currentFeatureKey = FF4J_TAG + "." + FEATURES_TAG  + "." + idx;
    while (mapConf.containsKey(currentFeatureKey +  "." + FEATURE_ATT_UID)) {
        assertKeyNotEmpty(mapConf, currentFeatureKey +  "." + FEATURE_ATT_UID);
        Feature f = new Feature(mapConf.get(currentFeatureKey +  "." + FEATURE_ATT_UID));
        // Enabled
        assertKeyNotEmpty(mapConf, currentFeatureKey +  "." + FEATURE_ATT_ENABLE);
        f.setEnable(Boolean.valueOf(mapConf.get(currentFeatureKey +  "." + FEATURE_ATT_ENABLE)));
        // Description
        String description = mapConf.get(currentFeatureKey +  "." + FEATURE_ATT_DESC);
        if (null != description && !"".equals(description)) {
            f.setDescription(description);
        }
        // Group
        String groupName = mapConf.get(currentFeatureKey +  "." + FEATURE_ATT_GROUP);
        if (null != groupName && !"".equals(groupName)) {
            f.setGroup(groupName);
        }
        // Permissions
        String strPermissions = mapConf.get(currentFeatureKey +  "." + FEATURE_ATT_PERMISSIONS);
        if (null != strPermissions && !"".equals(strPermissions)) {
            f.setPermissions(
                    Arrays.asList(strPermissions.split(","))
                          .stream()
                          .map(String::trim)
                          .collect(Collectors.toSet()));
        }
        // Custom Properties
        f.setCustomProperties(parseProperties(currentFeatureKey + "." + FEATURE_ATT_PROPERTIES, mapConf));
        // FlipStrategy
        String flipStrategyClass = mapConf.get(currentFeatureKey +  "." + TOGGLE_STRATEGY_TAG + "." + TOGGLE_STRATEGY_ATTCLASS);
        if (null != flipStrategyClass && !"".equals(flipStrategyClass)) {
            FlippingStrategy flipStrategy = null;
            try {
                flipStrategy = (FlippingStrategy) Class.forName(flipStrategyClass).newInstance();
            } catch (Exception e) {
                throw new IllegalArgumentException("Cannot parse flipStrategy for feature '" + f.getUid() + 
                        "' -> check key [" + currentFeatureKey +  "." + TOGGLE_STRATEGY_TAG + "." + TOGGLE_STRATEGY_ATTCLASS + "]", e);
            }
            int idxParam = 0;
            String currentParamKey = currentFeatureKey +  "." + TOGGLE_STRATEGY_TAG + "." + TOGGLE_STRATEGY_PARAMTAG + "." + idxParam;
            Map<String, String> params = new HashMap<>();
            while (mapConf.containsKey(currentParamKey+  "." + TOGGLE_STRATEGY_PARAMNAME)) {
                assertKeyNotEmpty(mapConf, currentParamKey + "." + TOGGLE_STRATEGY_PARAMNAME);
                assertKeyNotEmpty(mapConf, currentParamKey + "." + TOGGLE_STRATEGY_PARAMVALUE);
                params.put(mapConf.get(currentParamKey + "." + TOGGLE_STRATEGY_PARAMNAME), 
                           mapConf.get(currentParamKey + "." + TOGGLE_STRATEGY_PARAMVALUE));
                currentParamKey = currentFeatureKey +  "." + TOGGLE_STRATEGY_TAG + "." + TOGGLE_STRATEGY_PARAMTAG + "." + ++idxParam;
            }
            flipStrategy.init(f.getUid(), params);
            f.setFlippingStrategy(flipStrategy);

        }
        ff4jConfig.getFeatures().put(f.getUid(), f);

        // ff4j.features.X
        currentFeatureKey = FF4J_TAG + "." + FEATURES_TAG  + "." + ++idx;
    }

}

private Map < String, Property<?>> parseProperties(String prefix, Map<String, String> mapConfigProperties) {
    Map < String, Property<?>> result = new HashMap<>();
    int idx = 0;
    String currentPropertyKey = prefix  + "." + idx;
    while (mapConfigProperties.containsKey(currentPropertyKey +  "." + PROPERTY_PARAMNAME)) {

        assertKeyNotEmpty(mapConfigProperties, currentPropertyKey +  "." + PROPERTY_PARAMNAME);
        String name     = mapConfigProperties.get(currentPropertyKey +  "." + PROPERTY_PARAMNAME);

        assertKeyNotEmpty(mapConfigProperties, currentPropertyKey +  "." + PROPERTY_PARAMVALUE);
        String strValue = mapConfigProperties.get(currentPropertyKey +  "." + PROPERTY_PARAMVALUE);

        Property<?> ap      = new PropertyString(name, strValue);
        String optionalType = mapConfigProperties.get(currentPropertyKey + "." + PROPERTY_PARAMTYPE);
        // If specific type defined ?
        if (null != optionalType) {
            // Substitution if relevant (e.g. 'int' -> 'org.ff4j.property.PropertyInt')
            optionalType = MappingUtil.mapPropertyType(optionalType);
            try {
                // Constructor (String, String) is mandatory in Property interface
                Constructor<?> constr = Class.forName(optionalType).getConstructor(String.class, String.class);
                ap = (Property<?>) constr.newInstance(name, strValue);
            } catch (Exception e) {
                throw new IllegalArgumentException("Cannot instantiate '" + optionalType + "' check default constructor", e);
            }
        }
        // Description
        String description = mapConfigProperties.get(currentPropertyKey + "." + PROPERTY_PARAMDESCRIPTION);
        if (null != description) {
            ap.setDescription(description);
        } 
        // Fixed Values
        String strFixedValues = mapConfigProperties.get(currentPropertyKey + "." + PROPERTY_PARAMFIXED_VALUES);
        if (null != strFixedValues && !"".equals(strFixedValues)) {
            Arrays.asList(strValue.split(","))
                  .stream()
                  .map(String::trim)
                  .forEach(ap::add2FixedValueFromString);
        }

        // Check fixed value
        if (ap.getFixedValues() != null &&  
           !ap.getFixedValues().isEmpty() && 
           !ap.getFixedValues().contains(ap.getValue())) {
            throw new IllegalArgumentException("Cannot create property <" + ap.getName() + 
                    "> invalid value <" + ap.getValue() + 
                    "> expected one of " + ap.getFixedValues());
        }
        result.put(ap.getName(), ap);
        // ff4j.properties.X
        currentPropertyKey = prefix + "." + ++idx;
    }
    return result;

