public String execute( final Context context, final Map< String, String > params ) throws PluginException {
    final String title = params.get( PARAM_TITLE );
    String description = params.get( PARAM_DESCRIPTION );
    String version = params.get( PARAM_VERSION );
    String submitter = null;
    final SimpleDateFormat format = new SimpleDateFormat( DEFAULT_DATEFORMAT );
    final ResourceBundle rb = Preferences.getBundle( context, Plugin.CORE_PLUGINS_RESOURCEBUNDLE );
    final Principal wup = context.getCurrentUser();

    if( wup != null ) {
        submitter = wup.getName();
    }

    if( title == null ) {
        throw new PluginException(rb.getString("bugreporthandler.titlerequired"));
    }
    if( title.isEmpty() ) {
        return "";
    }

    if( description == null ) {
        description = "";
    }
    if( version == null ) {
        version = "unknown";
    }

    final Properties mappings = parseMappings( params.get( PARAM_MAPPINGS ) );

    //  Start things
    try {
        final StringWriter str = new StringWriter();
        final PrintWriter out = new PrintWriter( str );
        final Date d = new Date();

        //  Outputting of basic data
        out.println( "|" + mappings.getProperty(PARAM_TITLE,"Title" ) + "|" + title );
        out.println( "|" + mappings.getProperty("date","Date" ) + "|" + format.format( d ) );
        out.println( "|" + mappings.getProperty(PARAM_VERSION,"Version" ) + "|" + version );
        if( submitter != null ) {
            out.println("|"+mappings.getProperty("submitter","Submitter") + "|" + submitter );
        }

        //  Outputting the other parameters added to this.
        for( final Map.Entry< String, String > entry : params.entrySet() ) {
            if( !( entry.getKey().equals( PARAM_TITLE ) ||
                   entry.getKey().equals( PARAM_DESCRIPTION ) ||
                   entry.getKey().equals( PARAM_VERSION ) ||
                   entry.getKey().equals( PARAM_MAPPINGS ) ||
                   entry.getKey().equals( PARAM_PAGE ) ||
                   entry.getKey().startsWith( "_" )
                 ) ) {
                //  If no mapping has been defined, just ignore it.
                final String head = mappings.getProperty( entry.getKey(), entry.getKey() );
                if( !head.isEmpty() ) {
                    out.println( "|" + head + "|" + entry.getValue() );
                }
            }
        }

        out.println();
        out.println( description );
        out.close();

        //  Now create a new page for this bug report
        final String pageName = findNextPage( context, title, params.get( PARAM_PAGE ) );
        final Page newPage = Wiki.contents().page( context.getEngine(), pageName );
        final Context newContext = context.clone();
        newContext.setPage( newPage );
        context.getEngine().getManager( PageManager.class ).saveText( newContext, str.toString() );

        final MessageFormat formatter = new MessageFormat("");
        formatter.applyPattern( rb.getString("bugreporthandler.new") );
        final String[] args = { "<a href=\""+context.getViewURL(pageName)+"\">"+pageName+"</a>" };

        return formatter.format( args );
    } catch( final RedirectException e ) {
        LOG.info("Saving not allowed, reason: '"+e.getMessage()+"', can't redirect to "+e.getRedirect());
        throw new PluginException("Saving not allowed, reason: "+e.getMessage());
    } catch( final WikiException e ) {
        LOG.error( "Unable to save page!", e );
        return rb.getString("bugreporthandler.unable" );
    }
}

public String execute( final Context context, final Map<String, String> params ) throws PluginException {
    final Engine engine = context.getEngine();

    final StringBuilder res = new StringBuilder();

    final String clazz        = params.get( PARAM_CLASS );
    final String includedPage = params.get( PARAM_PAGENAME );
    String style              = params.get( PARAM_STYLE );
    final boolean showOnce    = "once".equals( params.get( PARAM_SHOW ) );
    final String defaultstr   = params.get( PARAM_DEFAULT );
    final int section         = TextUtil.parseIntParameter(params.get( PARAM_SECTION ), -1 );
    int maxlen                = TextUtil.parseIntParameter(params.get( PARAM_MAXLENGTH ), -1 );

    final ResourceBundle rb = Preferences.getBundle( context, Plugin.CORE_PLUGINS_RESOURCEBUNDLE );

    if( style == null ) {
        style = DEFAULT_STYLE;
    }

    if( maxlen == -1 ) {
        maxlen = Integer.MAX_VALUE;
    }

    if( includedPage != null ) {
        final Page page;
        try {
            final String pageName = engine.getFinalPageName( includedPage );
            if( pageName != null ) {
                page = engine.getManager( PageManager.class ).getPage( pageName );
            } else {
                page = engine.getManager( PageManager.class ).getPage( includedPage );
            }
        } catch( final ProviderException e ) {
            res.append( "<span class=\"error\">Page could not be found by the page provider.</span>" );
            return res.toString();
        }

        if( page != null ) {
            //  Check for recursivity
            List<String> previousIncludes = context.getVariable( ATTR_RECURSE );

            if( previousIncludes != null ) {
                if( previousIncludes.contains( page.getName() ) ) {
                    return "<span class=\"error\">Error: Circular reference - you can't include a page in itself!</span>";
                }
            } else {
                previousIncludes = new ArrayList<>();
            }

            // Check for permissions
            final AuthorizationManager mgr = engine.getManager( AuthorizationManager.class );

            if( !mgr.checkPermission( context.getWikiSession(), PermissionFactory.getPagePermission( page, "view") ) ) {
                res.append("<span class=\"error\">You do not have permission to view this included page.</span>");
                return res.toString();
            }

            // Show Once
            // Check for page-cookie, only include page if cookie is not yet set
            String cookieName = "";

            if( showOnce ) {
                cookieName = ONCE_COOKIE + TextUtil.urlEncodeUTF8( page.getName() ).replaceAll( "\\+", "%20" );

                if( HttpUtil.retrieveCookieValue( context.getHttpRequest(), cookieName ) != null ) {
                    return "";  //silent exit
                }

            }

            // move here, after premature exit points (permissions, page-cookie)
            previousIncludes.add( page.getName() );
            context.setVariable( ATTR_RECURSE, previousIncludes );

            /**
             *  We want inclusion to occur within the context of
             *  its own page, because we need the links to be correct.
             */

            final Context includedContext = context.clone();
            includedContext.setPage( page );

            String pageData = engine.getManager( PageManager.class ).getPureText( page );
            String moreLink = "";

            if( section != -1 ) {
                try {
                    pageData = TextUtil.getSection( pageData, section );
                } catch( final IllegalArgumentException e ) {
                    throw new PluginException( e.getMessage() );
                }
            }

            if( pageData.length() > maxlen ) {
                pageData = pageData.substring( 0, maxlen )+" ...";
                moreLink = "<p><a href=\""+context.getURL( ContextEnum.PAGE_VIEW.getRequestContext(),includedPage)+"\">"+rb.getString("insertpage.more")+"</a></p>";
            }

            res.append("<div class=\"inserted-page ");
            if( clazz != null ) res.append( clazz );
            if( !style.equals(DEFAULT_STYLE) ) res.append( "\" style=\"" ).append( style );
            if( showOnce ) res.append( "\" data-once=\"" ).append( cookieName );
            res.append("\" >");

            res.append( engine.getManager( RenderingManager.class ).textToHTML( includedContext, pageData ) );
            res.append( moreLink );

            res.append("</div>");

            //
            //  Remove the name from the stack; we're now done with this.
            //
            previousIncludes.remove( page.getName() );
            context.setVariable( ATTR_RECURSE, previousIncludes );
        } else {
            if( defaultstr != null ) {
                res.append( defaultstr );
            } else {
                res.append( "There is no page called '" ).append( includedPage ).append( "'.  Would you like to " );
                res.append( "<a href=\"" ).append( context.getURL( ContextEnum.PAGE_EDIT.getRequestContext(), includedPage ) ).append( "\">create it?</a>" );
            }
        }
    } else {
        res.append( "<span class=\"error\">" );
        res.append( "You have to define a page!" );
        res.append( "</span>" );
    }
    return res.toString();
}

public String execute( final Context ctx, final Map< String, String > params ) throws PluginException {
    //  If there is no HTTP request, returns immediately.
    if( ctx.getHttpRequest() == null ) {
        return "";
    }
    final ResourceBundle rb = Preferences.getBundle( ctx, Plugin.CORE_PLUGINS_RESOURCEBUNDLE );

    // If we are NOT here due to this form being submitted, we do nothing.
    // The submitted form MUST have parameter 'formname' equal to the name
    // parameter of this Form plugin.

    final String formName   = params.get( PARAM_FORM );
    final String submitForm = ctx.getHttpParameter( PARAM_FORMNAMEHIDDEN );
    final String populator  = params.get( PARAM_POPULATE );

    if( formName == null || !formName.equals( submitForm ) ) {
        // No submitForm -> this was not a submission from the
        // generated form.  If populate is specified, we'll go
        // ahead and let the handler (populator) put stuff into
        // the context, otherwise we'll just hide.
        if( !PARAM_HANDLER.equals( populator ) )
            return "";
        // If population was allowed, we should first  
    }

    final String handler = params.get( PARAM_HANDLER );
    if( handler == null || handler.isEmpty() ) {
        // Need to print out an error here as this form is misconfigured
        return "<p class=\"error\">" + MessageFormat.format( rb.getString( "formoutput.missingargument" ), PARAM_HANDLER ) + "</p>";
    }

    final String sourcePage = ctx.getPage().getName();
    final String submitServlet = ctx.getURL( ContextEnum.PAGE_VIEW.getRequestContext(), sourcePage );

    // If there is previous FormInfo available - say, from a
    // FormSet plugin - use it.
    FormInfo info = getFormInfo( ctx );
    if( info == null ) {
        // Reconstruct the form info from post data
        info = new FormInfo();
        info.setName( formName );
    }
    // Force override of handler and submit.
    info.setHandler( handler );
    info.setAction( submitServlet );

    // Sift out all extra parameters, leaving only those submitted in the HTML FORM.
    final Map< String, String > handlerParams = FormUtil.requestToMap( ctx.getHttpRequest(), HANDLERPARAM_PREFIX );
    // Previous submission info may be available from FormSet
    // plugin - add, don't replace.
    info.addSubmission( handlerParams );

    // Pass the _body parameter from FormOutput on to the handler
    info.getSubmission().put( DefaultPluginManager.PARAM_BODY, params.get(DefaultPluginManager.PARAM_BODY ) );

    String handlerOutput = null;
    String error = null;
    try {
        // The plugin _can_ modify the parameters, so we make sure they stay with us.
        final PluginManager pm = ctx.getEngine().getManager( PluginManager.class );
        handlerOutput = pm.execute( ctx, handler, info.getSubmission() );
        info.setResult( handlerOutput );
        info.setStatus( FormInfo.EXECUTED );
    } catch( final PluginException pe ) {
        error = "<p class=\"error\">" + pe.getMessage() + "</p>";
        info.setError( error );
        info.setStatus( FormInfo.ERROR );
    }

    // We store the forminfo, so following Form plugin invocations on this
    // page can decide what to do based on its values.
    storeFormInfo( ctx, info );

    if( error != null )
        return error;

    return handlerOutput != null ? handlerOutput : "";
}

public String execute( final Context ctx, final Map< String, String > params ) throws PluginException {
    final ResourceBundle rb = Preferences.getBundle( ctx, Plugin.CORE_PLUGINS_RESOURCEBUNDLE );
    final String formName = params.get( PARAM_FORM );
    if( formName == null ) {
        throw new PluginException( MessageFormat.format( rb.getString( "formopen.missingparam" ), PARAM_FORM ) );
    }
    final String hide     = params.get( PARAM_HIDEFORM );
    final String sourcePage = ctx.getPage().getName();
    String submitServlet = params.get( PARAM_SUBMITHANDLER );
    if( submitServlet == null )
        submitServlet = ctx.getURL( ContextEnum.PAGE_VIEW.getRequestContext(), sourcePage );

    String method = params.get( PARAM_METHOD );
    if( method == null ) {
        method="post";
    }

    if( !( method.equalsIgnoreCase( "get" ) || method.equalsIgnoreCase( "post" ) ) ) {
        throw new PluginException( rb.getString( "formopen.postorgetonly" ) );
    }

    FormInfo info = getFormInfo( ctx );
    if( info != null ) {
        // Previous information may be the result of submitting
        // this form, or of a FormSet plugin, or both. If it
        // exists and is for this form, fine.
        if( formName.equals( info.getName() ) ) {
            LOG.debug( "Previous FormInfo for this form was found in context." );
            // If the FormInfo exists, and if we're supposed to display on error only, we need to exit now.
            if( HIDE_SUCCESS.equals( hide ) && info.getStatus() == FormInfo.EXECUTED ) {
                info.setHide( true );
                return "<p>" + rb.getString( "formopen.noneedtoshow" ) + "</p>";
            }
        } else {
            // This would mean that a new form was started without closing an old one.  Get rid of the garbage.
            info = new FormInfo();
        }
    } else {
        // No previous FormInfo available; store now, so it'll be available for upcoming Form input elements.
        info = new FormInfo();
        storeFormInfo( ctx, info );
    }

    info.setName( formName );
    info.setAction( submitServlet );

    return "<div class=\"wikiform\">\n" +
              "<form action=\"" + submitServlet + "\" name=\"" + formName + "\" " +
