              "  <input type=\"hidden\" name=\"" + CsrfProtectionFilter.ANTICSRF_PARAM + "\" value=\"" + ctx.getWikiSession().antiCsrfToken() + "\"/>\n";
}

public String execute( final Context context, final Map< String, String > params ) throws PluginException {
    final String formatString = params.get( "format" );

    try {
        final SimpleDateFormat fmt;
        if( formatString != null ) {
            fmt = new SimpleDateFormat( formatString );
        } else {
            fmt = Preferences.getDateFormat( context, TimeFormat.DATETIME );
        }

        final Date d = new Date();  // Now.

        return fmt.format( d );
    } catch( final IllegalArgumentException e ) {
        final ResourceBundle rb = Preferences.getBundle( context, Plugin.CORE_PLUGINS_RESOURCEBUNDLE );
        throw new PluginException( rb.getString( "currenttimeplugin.badformat" ) + e.getMessage() );
    }
}

public String execute( final Context context, final Map<String, String> params ) throws PluginException {
    int maxItems = Integer.MAX_VALUE;
    final Collection< SearchResult > results;

    final String queryString = params.get( PARAM_QUERY );
    String set               = params.get( PARAM_SET );
    final String max         = params.get( PARAM_MAX );

    if ( set == null ) set = DEFAULT_SETNAME;
    if ( max != null ) maxItems = Integer.parseInt( max );

    if ( queryString == null ) {
        results = context.getVariable( set );
    } else {
        try {
            results = doBasicQuery( context, queryString );
            context.setVariable( set, results );
        } catch( final Exception e ) {
            return "<div class='error'>" + e.getMessage() + "</div>\n";
        }
    }

    String res = "";

    if ( results != null ) {
        res = renderResults(results,context,maxItems);
    }

    return res;
}

public void execute() throws IOException {
  GzipCompressorInputStream gzipIn;
  try {
    gzipIn = new GzipCompressorInputStream(new FileInputStream(source));
  } catch (Exception e) {
    throw new IOException("Error reading " + source + ": " + e.getMessage(), e);
  }
  Map<String, byte[]> content = new HashMap<>();

  try (TarArchiveInputStream tarIn = new TarArchiveInputStream(gzipIn)) {
    TarArchiveEntry entry;

    while ((entry = (TarArchiveEntry) tarIn.getNextEntry()) != null) {
      String n = entry.getName();
      if (!entry.isDirectory()) {
        int count;
        byte[] data = new byte[BUFFER_SIZE];
        ByteArrayOutputStream fos = new ByteArrayOutputStream();
        try (BufferedOutputStream dest = new BufferedOutputStream(fos, BUFFER_SIZE)) {
          while ((count = tarIn.read(data, 0, BUFFER_SIZE)) != -1) {
            dest.write(data, 0, count);
          }
        }
        fos.close();
        content.put(n, fos.toByteArray());
      }
    }
  }

  Map<String, byte[]> output = new HashMap<>();
  output.put("package/package.json", convertPackage(content.get("package/package.json")));
  output.put("package/other/spec.internals", convertSpec(content.get("package/other/spec.internals")));

  for (Entry<String, byte[]> e : content.entrySet()) {
    if (!e.getKey().equals("package/package.json") && !e.getKey().equals("package/other/spec.internals")) {
      byte[] cnv = e.getValue();
      try {
        JsonObject json = JsonParser.parseObject(e.getValue());
        if (json.has("resourceType")) {
          cnv = convertResource(e.getKey(), e.getValue());
        }
      } catch (Exception ex) {
      }
      if (cnv != null && cnv.length > 0) {
        output.put(e.getKey(), cnv);
      }
    }
  }

  TarArchiveOutputStream tar;
  ByteArrayOutputStream OutputStream;
  BufferedOutputStream bufferedOutputStream;
  GzipCompressorOutputStream gzipOutputStream;

  OutputStream = new ByteArrayOutputStream();
  bufferedOutputStream = new BufferedOutputStream(OutputStream);
  gzipOutputStream = new GzipCompressorOutputStream(bufferedOutputStream);
  tar = new TarArchiveOutputStream(gzipOutputStream);


  Map<String, NpmPackageIndexBuilder> indexers = new HashMap<>();
  for (Entry<String, byte[]> e : output.entrySet()) {
    String n = e.getKey().substring(0, e.getKey().lastIndexOf("/"));
    String s = e.getKey().substring(n.length() + 1);
    byte[] b = e.getValue();
    NpmPackageIndexBuilder indexer = indexers.get(n);
    if (indexer == null) {
      indexer = new NpmPackageIndexBuilder();
      indexer.start();
      indexers.put(n, indexer);
    }
    indexer.seeFile(s, b);
    if (!s.equals(".index.json") && !s.equals("package.json")) {
      TarArchiveEntry entry = new TarArchiveEntry(e.getKey());
      entry.setSize(b.length);
      tar.putArchiveEntry(entry);
      tar.write(b);
      tar.closeArchiveEntry();
    }
  }
  for (Entry<String, NpmPackageIndexBuilder> e : indexers.entrySet()) {
    byte[] cnt = e.getValue().build().getBytes(StandardCharsets.UTF_8);
    TarArchiveEntry entry = new TarArchiveEntry(e.getKey() + "/.index.json");
    entry.setSize(cnt.length);
    tar.putArchiveEntry(entry);
    tar.write(cnt);
    tar.closeArchiveEntry();
  }

  byte[] cnt = output.get("package/package.json");
  TarArchiveEntry entry = new TarArchiveEntry("package/package.json");
  entry.setSize(cnt.length);
  tar.putArchiveEntry(entry);
  tar.write(cnt);
  tar.closeArchiveEntry();

  tar.finish();
  tar.close();
  gzipOutputStream.close();
  bufferedOutputStream.close();
  OutputStream.close();
  byte[] b = OutputStream.toByteArray();
  TextFile.bytesToFile(b, dest);
}

