public static void unzip(InputStream is, String targetDir) throws IOException {
  try (ZipInputStream zipIn = new ZipInputStream(is)) {
    for (ZipEntry ze; (ze = zipIn.getNextEntry()) != null; ) {
      String path = Path.of(Utilities.path(targetDir, ze.getName())).normalize().toFile().getAbsolutePath();
      if (!path.startsWith(targetDir)) {
        // see: https://snyk.io/research/zip-slip-vulnerability
        throw new RuntimeException("Entry with an illegal path: " + ze.getName());
      }
      if (ze.isDirectory()) {
        Utilities.createDirectory(path);
      } else {
        Utilities.createDirectory(Utilities.getDirectoryForFile(path));
        TextFile.streamToFileNoClose(zipIn, path);
      }
    }
  }
}

public void testRoundTrip(String rootDir, String tmpDir, Collection<String> names) throws Throwable {
	try {
		System.err.println("Round trip from "+rootDir+" to "+tmpDir+":"+Integer.toString(names.size())+" files");
		for (String n : names) {
			System.err.print("  "+n);
			String source = rootDir + n + ".xml";
			// String tmpJson = tmpDir + n + ".json";
			String tmp = tmpDir + n.replace(File.separator, "-") + ".tmp";
			String dest = tmpDir + n.replace(File.separator, "-") + ".java.xml";

			FileInputStream in = new FileInputStream(source);
			XmlParser xp = new XmlParser();
			Resource r = xp.parse(in);
			System.err.print(".");
			JsonParser jp = new JsonParser();
			FileOutputStream out = new FileOutputStream(tmp);
			jp.setOutputStyle(OutputStyle.PRETTY);
			jp.compose(out, r);
			out.close();
			r = null;
			System.err.print(".");

			in = new FileInputStream(tmp);
			System.err.print(",");
			r = jp.parse(in);
			System.err.print(".");
			out = new FileOutputStream(dest);
			new XmlParser().compose(out, r, true);
			System.err.println("!");
			out.close();
			r = null;
			System.gc();
		}
	} catch (Throwable e) {
		System.err.println("Error: "+e.getMessage());
		throw e;
	}
}

