    }

    return scanner;
}

public static List<SubsystemFactory> resolveServerSubsystems(
        ServerFactoryManager server, Level level, PrintStream stdout, PrintStream stderr, PropertyResolver options)
        throws Exception {
    ClassLoader cl = ThreadUtils.resolveDefaultClassLoader(SubsystemFactory.class);
    String classList = System.getProperty(SubsystemFactory.class.getName());
    if (GenericUtils.isNotEmpty(classList)) {
        String[] classes = GenericUtils.split(classList, ',');
        List<SubsystemFactory> subsystems = new ArrayList<>(classes.length);
        for (String fqcn : classes) {
            try {
                Class<?> clazz = cl.loadClass(fqcn);
                SubsystemFactory factory = ReflectionUtils.newInstance(clazz, SubsystemFactory.class);
                factory = registerSubsystemFactoryListeners(
                        server, level, stdout, stderr, options, factory);
                subsystems.add(factory);
            } catch (Exception e) {
                stderr.append("ERROR: Failed (").append(e.getClass().getSimpleName()).append(')')
                        .append(" to instantiate subsystem=").append(fqcn)
                        .append(": ").println(e.getMessage());
                stderr.flush();
                throw e;
            }
        }

        return subsystems;
    }

    String nameList = (options == null) ? null : options.getString(ConfigFileReaderSupport.SUBSYSTEM_CONFIG_PROP);
    if (PropertyResolverUtils.isNoneValue(nameList)) {
        return Collections.emptyList();
    }

    if (SftpConstants.SFTP_SUBSYSTEM_NAME.equalsIgnoreCase(nameList)) {
        SubsystemFactory factory = registerSubsystemFactoryListeners(
                server, level, stdout, stderr, options, new SftpSubsystemFactory());
        PrintStream logStream = CliLogger.resolvePrintStream(level, stdout, stderr);
        CliLogger.log(logStream, level, "Using built-in SFTP subsystem");
        return Collections.singletonList(factory);
    }

    boolean havePreferences = GenericUtils.isNotEmpty(nameList);
    Collection<String> preferredNames = (!havePreferences)
            ? Collections.emptySet()
            : Stream.of(GenericUtils.split(nameList, ','))
                    .collect(Collectors.toCollection(() -> new TreeSet<>(String.CASE_INSENSITIVE_ORDER)));
    ServiceLoader<SubsystemFactory> loader = ServiceLoader.load(SubsystemFactory.class, cl);
    List<SubsystemFactory> subsystems = new ArrayList<>();
    for (SubsystemFactory factory : loader) {
        String name = factory.getName();
        if (havePreferences && (!preferredNames.contains(name))) {
            continue;
        }

        registerSubsystemFactoryListeners(server, level, stdout, stderr, options, factory);
        subsystems.add(factory);
    }

    return subsystems;
}

public static void main(String[] args) throws Exception {
    int port = 8000;
    boolean error = false;
    String hostKeyType = AbstractGeneratorHostKeyProvider.DEFAULT_ALGORITHM;
    int hostKeySize = 0;
    Collection<String> keyFiles = null;
    Collection<String> certFiles = null;
    Map<String, Object> options = new TreeMap<>(String.CASE_INSENSITIVE_ORDER);

    int numArgs = GenericUtils.length(args);
    for (int i = 0; i < numArgs; i++) {
        String argName = args[i];
        if ("-p".equals(argName)) {
            i++;
            if (i >= numArgs) {
                System.err.println("option requires an argument: " + argName);
                error = true;
                break;
            }
            port = Integer.parseInt(args[i]);
        } else if ("-key-type".equals(argName)) {
            i++;
            if (i >= numArgs) {
                System.err.println("option requires an argument: " + argName);
                error = true;
                break;
            }

            if (keyFiles != null) {
                System.err.println("option conflicts with -key-file: " + argName);
                error = true;
                break;
            }
            hostKeyType = args[i].toUpperCase();
        } else if ("-key-size".equals(argName)) {
            i++;
            if (i >= numArgs) {
                System.err.println("option requires an argument: " + argName);
                error = true;
                break;
            }

            if (keyFiles != null) {
                System.err.println("option conflicts with -key-file: " + argName);
                error = true;
                break;
            }

            hostKeySize = Integer.parseInt(args[i]);
        } else if ("-key-file".equals(argName)) {
            i++;
            if (i >= numArgs) {
                System.err.println("option requires an argument: " + argName);
                error = true;
                break;
            }

            String keyFilePath = args[i];
            if (keyFiles == null) {
                keyFiles = new LinkedList<>();
            }
            keyFiles.add(keyFilePath);
        } else if ("-o".equals(argName)) {
            i++;
            if (i >= numArgs) {
                System.err.println("option requires and argument: " + argName);
                error = true;
                break;
            }

            String opt = args[i];
            int idx = opt.indexOf('=');
            if (idx <= 0) {
                System.err.println("bad syntax for option: " + opt);
                error = true;
                break;
            }

            String optName = opt.substring(0, idx);
            String optValue = opt.substring(idx + 1);
            if (ServerIdentity.HOST_KEY_CONFIG_PROP.equals(optName)) {
                if (keyFiles == null) {
                    keyFiles = new LinkedList<>();
                }
                keyFiles.add(optValue);
            } else if (ServerIdentity.HOST_CERT_CONFIG_PROP.equals(optName)) {
                if (certFiles == null) {
                    certFiles = new LinkedList<>();
                }
                certFiles.add(optValue);
            } else if (ConfigFileReaderSupport.PORT_CONFIG_PROP.equals(optName)) {
                port = Integer.parseInt(optValue);
            } else {
                options.put(optName, optValue);
            }
        }
    }

    PropertyResolver resolver = PropertyResolverUtils.toPropertyResolver(options);
    Level level = CliLogger.resolveLoggingVerbosity(resolver, args);
    Logger logger = CliLogger.resolveSystemLogger(SshServerMain.class, level);
    SshServer sshd = error
            ? null
            : setupIoServiceFactory(
                    SshServer.setUpDefaultServer(), resolver,
                    level, System.out, System.err, args);
    if (sshd == null) {
        error = true;
    }

    if (error) {
        System.err.println(
                "usage: sshd [-p port] [-io mina|nio2|netty] [-key-type RSA|DSA|EC] [-key-size NNNN] [-key-file <path>] [-o option=value]");
        System.exit(-1);
        return; // just in case...
    }

    Map<String, Object> props = sshd.getProperties();
    props.putAll(options);

    SshServerConfigFileReader.configure(sshd, resolver, true, true);
    KeyPairProvider hostKeyProvider = resolveServerKeys(System.err, hostKeyType, hostKeySize, keyFiles);
    sshd.setKeyPairProvider(hostKeyProvider);
    if (GenericUtils.isNotEmpty(certFiles)) {
        HostKeyCertificateProvider certProvider = new FileHostKeyCertificateProvider(
                certFiles.stream().map(Paths::get).collect(Collectors.toList()));
        sshd.setHostKeyCertificateProvider(certProvider);
    }
    // Should come AFTER key pair provider setup so auto-welcome can be generated if needed
    setupServerBanner(sshd, resolver);
    sshd.setPort(port);

    ShellFactory shellFactory = resolveShellFactory(level, System.out, System.err, resolver);
    if (shellFactory != null) {
        if (logger.isInfoEnabled()) {
            logger.info("Using shell={}", shellFactory.getClass().getName());
        }
        sshd.setShellFactory(shellFactory);
    }

    sshd.setPasswordAuthenticator((username, password, session) -> Objects.equals(username, password));
    sshd.setPublickeyAuthenticator(AcceptAllPublickeyAuthenticator.INSTANCE);
    setupUserAuthFactories(sshd, resolver);
    setupServerForwarding(sshd, level, System.out, System.err, resolver);
    setupCommandFactory(sshd, level, System.out, System.err, shellFactory);

    List<SubsystemFactory> subsystems = resolveServerSubsystems(sshd, level, System.out, System.err, resolver);
    if (GenericUtils.isNotEmpty(subsystems)) {
        if (logger.isInfoEnabled()) {
            logger.info("Setup subsystems={}", NamedResource.getNames(subsystems));
        }
        sshd.setSubsystemFactories(subsystems);
    }

    System.err.println("Starting SSHD on port " + port);
    sshd.start();
    Thread.sleep(Long.MAX_VALUE);
    System.err.println("Exiting after a very (very very) long time");
}

public static void main(String[] args) throws Exception {
    int port = SshConstants.DEFAULT_PORT;
    boolean error = false;
    Map<String, Object> options = new TreeMap<>(String.CASE_INSENSITIVE_ORDER);
    int numArgs = GenericUtils.length(args);
    for (int i = 0; i < numArgs; i++) {
        String argName = args[i];
        if ("-p".equals(argName)) {
            if ((i + 1) >= numArgs) {
                System.err.println("option requires an argument: " + argName);
                break;
            }
            port = Integer.parseInt(args[++i]);
        } else if ("-io".equals(argName)) {
            if ((i + 1) >= numArgs) {
                System.err.println("option requires an argument: " + argName);
                break;
            }

            String provider = args[++i];
            BuiltinIoServiceFactoryFactories factory
                    = CliSupport.resolveBuiltinIoServiceFactory(System.err, argName, provider);
            if (factory != null) {
                System.setProperty(IoServiceFactory.class.getName(), factory.getFactoryClassName());
            } else {
                error = true;
                break;
            }
        } else if ("-o".equals(argName)) {
            if ((i + 1) >= numArgs) {
                System.err.println("option requires and argument: " + argName);
                error = true;
                break;
            }

            String opt = args[++i];
            int idx = opt.indexOf('=');
            if (idx <= 0) {
                System.err.println("bad syntax for option: " + opt);
                error = true;
                break;
            }
            options.put(opt.substring(0, idx), opt.substring(idx + 1));
        }
    }

    PropertyResolver resolver = PropertyResolverUtils.toPropertyResolver(options);
    Level level = CliLogger.resolveLoggingVerbosity(resolver, args);
    SshServer sshd = error
            ? null : setupIoServiceFactory(
                    CoreTestSupportUtils.setupTestServer(SshFsMounter.class), resolver,
                    level, System.out, System.err, args);
    if (sshd == null) {
        error = true;
    }

    if (error) {
        System.err.println("usage: sshfs [-p port] [-io mina|nio2|netty] [-o option=value]");
        System.exit(-1);
    }

    Map<String, Object> props = sshd.getProperties();
    props.putAll(options);
    Path targetFolder = Objects.requireNonNull(CommonTestSupportUtils.detectTargetFolder(MounterCommandFactory.class),
            "Failed to detect target folder");
    if (SecurityUtils.isBouncyCastleRegistered()) {
        sshd.setKeyPairProvider(SecurityUtils.createGeneratorHostKeyProvider(targetFolder.resolve("key.pem")));
    } else {
        sshd.setKeyPairProvider(new SimpleGeneratorHostKeyProvider(targetFolder.resolve("key.ser")));
    }
    // Should come AFTER key pair provider setup so auto-welcome can be generated if needed
    setupServerBanner(sshd, resolver);

    ShellFactory shellFactory = resolveShellFactory(level, System.out, System.err, resolver);
    if (shellFactory != null) {
        System.out.append("Using shell=").println(shellFactory.getClass().getName());
        sshd.setShellFactory(shellFactory);
    }
    sshd.setPasswordAuthenticator(AcceptAllPasswordAuthenticator.INSTANCE);
    sshd.setForwardingFilter(AcceptAllForwardingFilter.INSTANCE);

    ScpCommandFactory scpFactory;
    if (shellFactory instanceof ScpCommandFactory) {
        scpFactory = (ScpCommandFactory) shellFactory;
        scpFactory.setDelegateCommandFactory(MounterCommandFactory.INSTANCE);
    } else {
        scpFactory = new ScpCommandFactory.Builder()
                .withDelegate(MounterCommandFactory.INSTANCE)
                .build();
    }

    sshd.setCommandFactory(scpFactory);
    sshd.setSubsystemFactories(Collections.singletonList(new SftpSubsystemFactory()));
    sshd.setPort(port);

    System.err.println("Starting SSHD on port " + port);
    sshd.start();
    Thread.sleep(Long.MAX_VALUE);
}

