public static Path deleteRecursive(Path path, LinkOption... options) throws IOException {
    if ((path == null) || (!Files.exists(path))) {
        return path;
    }

    if (Files.isDirectory(path)) {
        try (DirectoryStream<Path> ds = Files.newDirectoryStream(path)) {
            for (Path child : ds) {
                deleteRecursive(child, options);
            }
        }
    }

    try {
        // seems that if a file is not writable it cannot be deleted
        if (!Files.isWritable(path)) {
            path.toFile().setWritable(true, false);
        }
        Files.delete(path);
    } catch (IOException e) {
        // same logic as deleteRecursive(File) which does not check if deletion succeeded
        System.err.append("Failed (").append(e.getClass().getSimpleName()).append(")")
                .append(" to delete ").append(path.toString())
                .append(": ").println(e.getMessage());
    }

    return path;
}

public void move(Path source, Path target, CopyOption... options) throws IOException {
    SftpPath src = toSftpPath(source);
    SftpFileSystem fsSrc = src.getFileSystem();
    SftpPath dst = toSftpPath(target);

    if (src.getFileSystem() != dst.getFileSystem()) {
        throw new ProviderMismatchException("Mismatched file system providers for " + src + " vs. " + dst);
    }
    checkAccess(src);

    boolean replaceExisting = false;
    boolean copyAttributes = false;
    boolean noFollowLinks = false;
    for (CopyOption opt : options) {
        replaceExisting |= opt == StandardCopyOption.REPLACE_EXISTING;
        copyAttributes |= opt == StandardCopyOption.COPY_ATTRIBUTES;
        noFollowLinks |= opt == LinkOption.NOFOLLOW_LINKS;
    }
    LinkOption[] linkOptions = IoUtils.getLinkOptions(noFollowLinks);

    // attributes of source file
    BasicFileAttributes attrs = readAttributes(source, BasicFileAttributes.class, linkOptions);
    if (attrs.isSymbolicLink()) {
        throw new IOException("Moving of source symbolic link (" + source + ") to " + target + " not supported");
    }

    // delete target if it exists and REPLACE_EXISTING is specified
    Boolean status = IoUtils.checkFileExists(target, linkOptions);
    if (status == null) {
        throw new AccessDeniedException("Existence cannot be determined for move target " + target);
    }

    if (log.isDebugEnabled()) {
        log.debug("move({})[{}] {} => {}", src.getFileSystem(), Arrays.asList(options), src, dst);
    }

    if (replaceExisting) {
        deleteIfExists(target);
    } else if (status) {
        throw new FileAlreadyExistsException(target.toString());
    }

    try (SftpClient sftp = fsSrc.getClient()) {
        sftp.rename(src.toString(), dst.toString());
    }

    // copy basic attributes to target
    if (copyAttributes) {
        BasicFileAttributeView view = getFileAttributeView(target, BasicFileAttributeView.class, linkOptions);
        try {
            view.setTimes(attrs.lastModifiedTime(), attrs.lastAccessTime(), attrs.creationTime());
        } catch (Throwable x) {
            // rollback
            try {
                delete(target);
            } catch (Throwable suppressed) {
                x.addSuppressed(suppressed);
            }
            throw x;
        }
    }
}

public void copy(Path source, Path target, CopyOption... options) throws IOException {
    SftpPath src = toSftpPath(source);
    SftpPath dst = toSftpPath(target);
    if (src.getFileSystem() != dst.getFileSystem()) {
        throw new ProviderMismatchException("Mismatched file system providers for " + src + " vs. " + dst);
    }
    checkAccess(src);

    boolean replaceExisting = false;
    boolean copyAttributes = false;
    boolean noFollowLinks = false;
    for (CopyOption opt : options) {
        replaceExisting |= opt == StandardCopyOption.REPLACE_EXISTING;
        copyAttributes |= opt == StandardCopyOption.COPY_ATTRIBUTES;
        noFollowLinks |= opt == LinkOption.NOFOLLOW_LINKS;
    }
    LinkOption[] linkOptions = IoUtils.getLinkOptions(!noFollowLinks);

    // attributes of source file
    BasicFileAttributes attrs = readAttributes(source, BasicFileAttributes.class, linkOptions);
    if (attrs.isSymbolicLink()) {
        throw new IOException("Copying of symbolic links not supported");
    }

    // delete target if it exists and REPLACE_EXISTING is specified
    Boolean status = IoUtils.checkFileExists(target, linkOptions);
    if (status == null) {
        throw new AccessDeniedException("Existence cannot be determined for copy target: " + target);
    }

    if (log.isDebugEnabled()) {
        log.debug("copy({})[{}] {} => {}", src.getFileSystem(), Arrays.asList(options), src, dst);
    }

    if (replaceExisting) {
        deleteIfExists(target);
    } else {
        if (status) {
            throw new FileAlreadyExistsException(target.toString());
        }
    }

    // create directory or copy file
    if (attrs.isDirectory()) {
        createDirectory(target);
    } else {
        CopyFileExtension copyFile = src.getFileSystem().getClient().getExtension(CopyFileExtension.class);
        if (copyFile.isSupported()) {
            copyFile.copyFile(source.toString(), target.toString(), false);
        } else {
            try (InputStream in = newInputStream(source);
                 OutputStream os = newOutputStream(target)) {
                IoUtils.copy(in, os);
            }
        }
    }

    // copy basic attributes to target
    if (copyAttributes) {
        BasicFileAttributeView view = getFileAttributeView(target, BasicFileAttributeView.class, linkOptions);
        try {
            view.setTimes(attrs.lastModifiedTime(), attrs.lastAccessTime(), attrs.creationTime());
        } catch (Throwable x) {
            // rollback
            try {
                delete(target);
            } catch (Throwable suppressed) {
                x.addSuppressed(suppressed);
            }
            throw x;
        }
    }
}

protected void writeDirEntry(
        ServerSession session, int id, Buffer buffer, int index, Path f, String shortName,
        SftpClient.Attributes attributes)
        throws IOException {
    int version = getVersion();

    SftpFileSystemAccessor accessor = getFileSystemAccessor();
    accessor.putRemoteFileName(this, f, buffer, shortName, true);

    if (version == SftpConstants.SFTP_V3) {
        String longName = getLongName(f, shortName, attributes);
        accessor.putRemoteFileName(this, f, buffer, longName, false);

        if (log.isTraceEnabled()) {
            log.trace("writeDirEntry({}) id={})[{}] - writing entry {} [{}]: {}", session, id, index, shortName, longName,
                    attributes);
        }
    } else {
        if (log.isTraceEnabled()) {
            log.trace("writeDirEntry({}) id={})[{}] - writing entry {}: {}", session, id, index, shortName, attributes);
        }
    }
    SftpHelper.writeAttributes(buffer, attributes, version);
}

protected NavigableMap<String, Object> handleUnknownStatusFileAttributes(
        Path file, int flags, LinkOption... options)
        throws IOException {
    UnsupportedAttributePolicy policy = getUnsupportedAttributePolicy();
    switch (policy) {
        case Ignore:
            break;
        case ThrowException:
            throw new AccessDeniedException(file.toString(), file.toString(),
                    "Cannot determine existence for attributes of target");
        case Warn:
            log.warn("handleUnknownStatusFileAttributes({})[{}] cannot determine existence", getServerSession(), file);
            break;
        default:
            log.warn("handleUnknownStatusFileAttributes({})[{}] unknown policy: {}", getServerSession(), file, policy);
    }

    return getAttributes(file, flags, options);
}

