protected SimpleImmutableEntry<Path, Boolean> validateRealPath(
        int id, String path, Path f, LinkOption... options)
        throws IOException {
    Path p = normalize(f);
    Boolean status = IoUtils.checkFileExists(p, options);
    return new SimpleImmutableEntry<>(p, status);
}

protected void doRemoveFile(int id, String path, boolean followLinks) throws IOException {
    Path resolvedPath = resolveFile(path);
    if (log.isDebugEnabled()) {
        log.debug("doRemoveFile({})[id={}] SSH_FXP_REMOVE (path={}[{}])", getServerSession(), id, path, resolvedPath);
    }

    SftpFileSystemAccessor accessor = getFileSystemAccessor();
    LinkOption[] options = accessor.resolveFileAccessLinkOptions(
            this, resolvedPath, SftpConstants.SSH_FXP_REMOVE, "", followLinks);
    SftpPathImpl.withAttributeCache(resolvedPath, p -> {
        Boolean status = IoUtils.checkFileExists(p, options);
        if (status == null) {
            throw signalRemovalPreConditionFailure(id, path, p,
                    new AccessDeniedException(p.toString(), p.toString(), "Cannot determine existence of remove candidate"),
                    false);
        } else if (!status) {
            throw signalRemovalPreConditionFailure(id, path, p,
                    new NoSuchFileException(p.toString(), p.toString(), "Removal candidate not found"), false);
        } else if (Files.isDirectory(p, options)) {
            throw signalRemovalPreConditionFailure(id, path, p,
                    new SftpException(SftpConstants.SSH_FX_FILE_IS_A_DIRECTORY, p.toString() + " is a folder"), false);
        }
        return null;
    });
    // File exists and is not a directory
    doRemove(id, resolvedPath, false);
}

protected void doReadDir(Buffer buffer, int id) throws IOException {
    String handle = buffer.getString();
    Handle h = handles.get(handle);
    ServerSession session = getServerSession();
    boolean debugEnabled = log.isDebugEnabled();
    if (debugEnabled) {
        log.debug("doReadDir({})[id={}] SSH_FXP_READDIR (handle={}[{}])", session, id, handle, h);
    }

    Buffer reply = null;
    try {
        DirectoryHandle dh = validateHandle(handle, h, DirectoryHandle.class);
        if (dh.isDone()) {
            sendStatus(prepareReply(buffer), id, SftpConstants.SSH_FX_EOF, "Directory reading is done");
            return;
        }

        Path file = dh.getFile();
        // If it's an SftpPath, don't re-check accessibily or existence. The underlying DirectoryHandle iterator
        // contacts the upstream server, which should check. This repeated check here is questionable anyway.
        // We did check in doOpenDir(). If access to the directory has changed in the meantime; it's undefined
        // anyway what happens. If the directory is local, it depends on what the Java library would do with
        // the DirectoryStream in that case if it reads the directory lazily. And if it is remote, it may well
        // be that the upstream server has read the whole list from the local file system and buffered it, so
        // it could serve the listing even if some other concurrent operation had removed the directory in the
        // meantime, or had changed its access. It is entirely unspecified what shall happen if files inside
        // the directory are changed while the directory is listed, and likewise it's entirely unspecified what
        // shall happen if the directory itself is deleted while being listed.
        //
        // As long as the file system is local, this check here is local operations only, but if the directory
        // is remote; this incurs several (up to three) remote LSTAT calls. We really can skip this here and let
        // the upstream server decide.
        if (!(file instanceof SftpPath)) {
            LinkOption[] options = getPathResolutionLinkOption(SftpConstants.SSH_FXP_READDIR, "", file);
            Boolean status = IoUtils.checkFileExists(file, options);
            if (status == null) {
                throw new AccessDeniedException(file.toString(), file.toString(), "Cannot determine existence of read-dir");
            }

            if (!status) {
                throw new NoSuchFileException(file.toString(), file.toString(), "Non-existent directory");
            } else if (!Files.isDirectory(file, options)) {
                throw new NotDirectoryException(file.toString());
            } else if (!Files.isReadable(file)) {
                throw new AccessDeniedException(file.toString(), file.toString(), "Not readable");
            }
        }

        SftpEventListener listener = getSftpEventListenerProxy();
        listener.readingEntries(session, handle, dh);

        if (dh.isSendDot() || dh.isSendDotDot() || dh.hasNext()) {
            // There is at least one file in the directory or we need to send the "..".
            // Send only a few files at a time to not create packets of a too
            // large size or have a timeout to occur.

            reply = prepareReply(buffer);
            reply.putByte((byte) SftpConstants.SSH_FXP_NAME);
            reply.putInt(id);

            int lenPos = reply.wpos();
            reply.putUInt(0L);  // save room for actual length

            int maxDataSize = SftpModuleProperties.MAX_READDIR_DATA_SIZE.getRequired(session);
            int count = doReadDir(id, handle, dh, reply, maxDataSize, false);
            BufferUtils.updateLengthPlaceholder(reply, lenPos, count);
            if ((!dh.isSendDot()) && (!dh.isSendDotDot()) && (!dh.hasNext())) {
                dh.markDone();
            }

            int sftpVersion = getVersion();
            Boolean indicator = SftpHelper.indicateEndOfNamesList(reply, sftpVersion, session, dh.isDone());
            if (debugEnabled) {
                log.debug("doReadDir({})({})[{}] - sending {} entries - eol={} (SFTP version {})", session, handle, h,
                        count, indicator, sftpVersion);
            }
        } else {
            // empty directory
            dh.markDone();
            sendStatus(prepareReply(buffer), id, SftpConstants.SSH_FX_EOF, "Empty directory");
            return;
        }

        Objects.requireNonNull(reply, "No reply buffer created");
    } catch (IOException | RuntimeException | Error e) {
        sendStatus(prepareReply(buffer), id, e, SftpConstants.SSH_FXP_READDIR, handle);
        return;
    }

    send(reply);
}

protected void doWrite(
        int id, String handle, long offset, int length, byte[] data, int doff, int remaining)
        throws IOException {
    Handle h = handles.get(handle);
    ServerSession session = getServerSession();
    int maxAllowed = SftpModuleProperties.MAX_WRITEDATA_PACKET_LENGTH.getRequired(session);
    if (log.isTraceEnabled()) {
        log.trace("doWrite({})[id={}] SSH_FXP_WRITE (handle={}[{}], offset={}, length={}, maxAllowed={})",
                session, id, handle, h, offset, length, maxAllowed);
    }

    FileHandle fh = validateHandle(handle, h, FileHandle.class);
    if (length < 0) {
        throw new IllegalStateException("Bad length (" + length + ") for writing to " + fh);
    }

    if (remaining < length) {
        throw new IllegalStateException("Not enough buffer data for writing to " + fh
                                        + ": required=" + length + ", available=" + remaining);
    }

    if (length > maxAllowed) {
        throw new IOException("Reuested write size (" + length + ") exceeds max. allowed (" + maxAllowed + ")");
    }

    SftpEventListener listener = getSftpEventListenerProxy();
    listener.writing(session, handle, fh, offset, data, doff, length);
    try {
        if (fh.isOpenAppend()) {
            fh.append(data, doff, length);
        } else {
            fh.write(data, doff, length, offset);
        }
    } catch (IOException | RuntimeException | Error e) {
        listener.written(session, handle, fh, offset, data, doff, length, e);
        throw e;
    }
    listener.written(session, handle, fh, offset, data, doff, length, null);
}

protected Map<String, Object> doFStat(int id, String handle, int flags) throws IOException {
    Handle h = handles.get(handle);
    if (log.isDebugEnabled()) {
        log.debug("doFStat({})[id={}] SSH_FXP_FSTAT (handle={}[{}], flags=0x{})",
                getServerSession(), id, handle, h, Integer.toHexString(flags));
    }

    Handle fileHandle = validateHandle(handle, h, Handle.class);
    SftpFileSystemAccessor accessor = getFileSystemAccessor();
    Path file = fileHandle.getFile();
    LinkOption[] options = accessor.resolveFileAccessLinkOptions(
            this, file, SftpConstants.SSH_FXP_FSTAT, "", true);
    return resolveFileAttributes(file, flags, options);
}

public void testSftpFileSystemAccessor() throws Exception {
    List<? extends SubsystemFactory> factories = sshd.getSubsystemFactories();
    assertEquals("Mismatched subsystem factories count", 1, GenericUtils.size(factories));

    SubsystemFactory f = factories.get(0);
    assertObjectInstanceOf("Not an SFTP subsystem factory", SftpSubsystemFactory.class, f);

    SftpSubsystemFactory factory = (SftpSubsystemFactory) f;
    SftpFileSystemAccessor accessor = factory.getFileSystemAccessor();
    try {
        AtomicReference<Path> fileHolder = new AtomicReference<>();
        AtomicReference<Path> dirHolder = new AtomicReference<>();
        factory.setFileSystemAccessor(new SftpFileSystemAccessor() {
            @Override
            public SeekableByteChannel openFile(
                    SftpSubsystemProxy subsystem, FileHandle fileHandle, Path file,
                    String handle, Set<? extends OpenOption> options, FileAttribute<?>... attrs)
                    throws IOException {
                fileHolder.set(file);
                return SftpFileSystemAccessor.super.openFile(
                        subsystem, fileHandle, file, handle, options, attrs);
            }

            @Override
            public DirectoryStream<Path> openDirectory(
                    SftpSubsystemProxy subsystem, DirectoryHandle dirHandle, Path dir, String handle)
                    throws IOException {
                dirHolder.set(dir);
                return SftpFileSystemAccessor.super.openDirectory(subsystem, dirHandle, dir, handle);
            }

            @Override
            public String toString() {
                return SftpFileSystemAccessor.class.getSimpleName() + "[" + getCurrentTestName() + "]";
            }
        });

        Path targetPath = detectTargetFolder();
        Path parentPath = targetPath.getParent();
        Path localFile = CommonTestSupportUtils.resolve(
                targetPath, SftpConstants.SFTP_SUBSYSTEM_NAME, getClass().getSimpleName(), getCurrentTestName());
        Files.createDirectories(localFile.getParent());
        byte[] expected = (getClass().getName() + "#" + getCurrentTestName() + "[" + localFile + "]")
                .getBytes(StandardCharsets.UTF_8);
        Files.write(localFile, expected, StandardOpenOption.CREATE);
        try (SftpClient sftp = createSingleSessionClient()) {
            byte[] actual = new byte[expected.length];
            try (InputStream stream = sftp.read(
                    CommonTestSupportUtils.resolveRelativeRemotePath(parentPath, localFile), OpenMode.Read)) {
                IoUtils.readFully(stream, actual);
            }

            Path remoteFile = fileHolder.getAndSet(null);
            assertNotNull("No remote file holder value", remoteFile);
            assertEquals("Mismatched opened local files", localFile.toFile(), remoteFile.toFile());
            assertArrayEquals("Mismatched retrieved file contents", expected, actual);

            Path localParent = localFile.getParent();
            String localName = Objects.toString(localFile.getFileName(), null);
            try (CloseableHandle handle = sftp.openDir(
                    CommonTestSupportUtils.resolveRelativeRemotePath(parentPath, localParent))) {
                List<DirEntry> entries = sftp.readDir(handle);
                Path remoteParent = dirHolder.getAndSet(null);
                assertNotNull("No remote folder holder value", remoteParent);
                assertEquals("Mismatched opened folder", localParent.toFile(), remoteParent.toFile());
                assertFalse("No dir entries", GenericUtils.isEmpty(entries));

                for (DirEntry de : entries) {
                    Attributes attrs = de.getAttributes();
                    if (!attrs.isRegularFile()) {
                        continue;
                    }

                    if (localName.equals(de.getFilename())) {
                        return;
                    }
                }

                fail("Cannot find listing of " + localName);
            }
        }
    } finally {
        factory.setFileSystemAccessor(accessor); // restore original
    }
}

private void testCannotEscapeRoot(boolean useAbsolutePath) throws Exception {
    Path targetPath = detectTargetFolder();
    Path lclSftp = CommonTestSupportUtils.resolve(
            targetPath, SftpConstants.SFTP_SUBSYSTEM_NAME, getClass().getSimpleName(), getCurrentTestName());
    lclSftp = assertHierarchyTargetFolderExists(lclSftp);
    sshd.setFileSystemFactory(new VirtualFileSystemFactory(lclSftp));

    String escapePath;
    if (useAbsolutePath) {
        escapePath = targetPath.toString();
        if (OsUtils.isWin32()) {
            escapePath = "/" + escapePath.replace(File.separatorChar, '/');
        }
    } else {
        Path parent = lclSftp.getParent();
        Path forbidden = Files.createDirectories(parent.resolve("forbidden"));
        escapePath = "../" + forbidden.getFileName();
    }

    try (SftpClient sftp = createSingleSessionClient()) {
        SftpClient.Attributes attrs = sftp.stat(escapePath);
        fail("Unexpected escape success for path=" + escapePath + ": " + attrs);
    } catch (SftpException e) {
        int expected = OsUtils.isWin32() || (!useAbsolutePath)
                ? SftpConstants.SSH_FX_INVALID_FILENAME
                : SftpConstants.SSH_FX_NO_SUCH_FILE;
        assertEquals("Mismatched status for " + escapePath,
                SftpConstants.getStatusName(expected),
                SftpConstants.getStatusName(e.getStatus()));
    }
}

public void testSftpExtensionsEncodeDecode() throws Exception {
    Class<?> anchor = getClass();
    Map<String, String> expExtensions = NavigableMapBuilder.<String, String> builder(String.CASE_INSENSITIVE_ORDER)
            .put("class", anchor.getSimpleName())
            .put("package", anchor.getPackage().getName())
            .put("method", getCurrentTestName())
            .build();

    final AtomicInteger numInvocations = new AtomicInteger(0);
    SftpSubsystemFactory factory = new SftpSubsystemFactory() {
        @Override
        public Command createSubsystem(ChannelSession channel) throws IOException {
            SftpSubsystem subsystem = new SftpSubsystem(channel, this) {
                @Override
                protected NavigableMap<String, Object> resolveFileAttributes(Path file, int flags, LinkOption... options)
                        throws IOException {
                    NavigableMap<String, Object> attrs = super.resolveFileAttributes(file, flags, options);
                    if (MapEntryUtils.isEmpty(attrs)) {
                        attrs = new TreeMap<>(String.CASE_INSENSITIVE_ORDER);
                    }

                    @SuppressWarnings("unchecked")
                    Map<String, String> actExtensions
                            = (Map<String, String>) attrs.put(IoUtils.EXTENDED_VIEW_ATTR, expExtensions);
                    if (actExtensions != null) {
                        log.info("resolveFileAttributes(" + file + ") replaced extensions: " + actExtensions);
                    }
                    return attrs;
                }

                @Override
                protected void setFileExtensions(Path file, Map<String, byte[]> extensions, LinkOption... options)
                        throws IOException {
                    assertExtensionsMapEquals("setFileExtensions(" + file + ")", expExtensions, extensions);
                    numInvocations.incrementAndGet();

                    int currentVersion = getTestedVersion();
                    try {
                        super.setFileExtensions(file, extensions, options);
                        assertFalse("Expected exception not generated for version=" + currentVersion,
                                currentVersion >= SftpConstants.SFTP_V6);
                    } catch (UnsupportedOperationException e) {
                        assertTrue("Unexpected exception for version=" + currentVersion,
                                currentVersion >= SftpConstants.SFTP_V6);
                    }
                }
            };
            Collection<? extends SftpEventListener> listeners = getRegisteredListeners();
            if (GenericUtils.size(listeners) > 0) {
                for (SftpEventListener l : listeners) {
                    subsystem.addSftpEventListener(l);
                }
            }

            return subsystem;
        }
    };

    factory.addSftpEventListener(new AbstractSftpEventListenerAdapter() {
        @Override
        public void modifyingAttributes(ServerSession session, Path path, Map<String, ?> attrs) {
            @SuppressWarnings("unchecked")
            Map<String, byte[]> actExtensions
                    = MapEntryUtils.isEmpty(attrs) ? null : (Map<String, byte[]>) attrs.get(IoUtils.EXTENDED_VIEW_ATTR);
            assertExtensionsMapEquals("modifying(" + path + ")", expExtensions, actExtensions);
        }

        @Override
        public void modifiedAttributes(ServerSession session, Path path, Map<String, ?> attrs, Throwable thrown) {
            @SuppressWarnings("unchecked")
            Map<String, byte[]> actExtensions
                    = MapEntryUtils.isEmpty(attrs) ? null : (Map<String, byte[]>) attrs.get(IoUtils.EXTENDED_VIEW_ATTR);
            assertExtensionsMapEquals("modified(" + path + ")", expExtensions, actExtensions);
        }
    });

    Path targetPath = detectTargetFolder();
    Path lclSftp
            = CommonTestSupportUtils.resolve(targetPath, SftpConstants.SFTP_SUBSYSTEM_NAME, getClass().getSimpleName());
    Files.createDirectories(lclSftp.resolve("sub-folder"));
    Path lclFile
            = assertHierarchyTargetFolderExists(lclSftp).resolve(getCurrentTestName() + "-" + getTestedVersion() + ".txt");
    Files.write(lclFile, getClass().getName().getBytes(StandardCharsets.UTF_8));

    Path parentPath = targetPath.getParent();
    String remotePath = CommonTestSupportUtils.resolveRelativeRemotePath(parentPath, lclSftp);
    int numInvoked = 0;

    List<? extends SubsystemFactory> factories = sshd.getSubsystemFactories();
    sshd.setSubsystemFactories(Collections.singletonList(factory));
    try (ClientSession session = createAuthenticatedClientSession();
         SftpClient sftp = createSftpClient(session, getTestedVersion())) {
        for (DirEntry entry : sftp.readDir(remotePath)) {
            String fileName = entry.getFilename();
            if (".".equals(fileName) || "..".equals(fileName)) {
                continue;
            }

            Attributes attrs = validateSftpFileTypeAndPermissions(fileName, getTestedVersion(), entry.getAttributes());
            Map<String, byte[]> actExtensions = attrs.getExtensions();
            assertExtensionsMapEquals("dirEntry=" + fileName, expExtensions, actExtensions);
            attrs.getFlags().clear();
            attrs.setStringExtensions(expExtensions);
            sftp.setStat(remotePath + "/" + fileName, attrs);
            numInvoked++;
        }
    } finally {
        sshd.setSubsystemFactories(factories);
    }

    assertEquals("Mismatched invocations count", numInvoked, numInvocations.get());
}

public void testSftpACLEncodeDecode() throws Exception {
    AclEntryType[] types = AclEntryType.values();
    final List<AclEntry> aclExpected = new ArrayList<>(types.length);
    for (AclEntryType t : types) {
        aclExpected.add(AclEntry.newBuilder()
                .setType(t)
                .setFlags(EnumSet.allOf(AclEntryFlag.class))
                .setPermissions(EnumSet.allOf(AclEntryPermission.class))
                .setPrincipal(new DefaultGroupPrincipal(getCurrentTestName() + "@" + getClass().getPackage().getName()))
                .build());
    }

    AtomicInteger numInvocations = new AtomicInteger(0);
    SftpSubsystemFactory factory = new SftpSubsystemFactory() {
        @Override
        public Command createSubsystem(ChannelSession channel) throws IOException {
            SftpSubsystem subsystem = new SftpSubsystem(channel, this) {
                @Override
                protected NavigableMap<String, Object> resolveFileAttributes(Path file, int flags, LinkOption... options)
                        throws IOException {
                    NavigableMap<String, Object> attrs = super.resolveFileAttributes(file, flags, options);
                    if (MapEntryUtils.isEmpty(attrs)) {
                        attrs = new TreeMap<>(String.CASE_INSENSITIVE_ORDER);
                    }

                    @SuppressWarnings("unchecked")
                    List<AclEntry> aclActual = (List<AclEntry>) attrs.put(IoUtils.ACL_VIEW_ATTR, aclExpected);
                    if (aclActual != null) {
                        log.info("resolveFileAttributes(" + file + ") replaced ACL: " + aclActual);
                    }
                    return attrs;
                }

                @Override
                protected void setFileAccessControl(Path file, List<AclEntry> aclActual, LinkOption... options)
                        throws IOException {
                    if (aclActual != null) {
                        assertListEquals("Mismatched ACL set for file=" + file, aclExpected, aclActual);
                        numInvocations.incrementAndGet();
                    }
                }
            };
            Collection<? extends SftpEventListener> listeners = getRegisteredListeners();
            if (GenericUtils.size(listeners) > 0) {
                for (SftpEventListener l : listeners) {
                    subsystem.addSftpEventListener(l);
                }
            }

            return subsystem;
        }
    };

    factory.addSftpEventListener(new AbstractSftpEventListenerAdapter() {
        @Override
        public void modifyingAttributes(ServerSession session, Path path, Map<String, ?> attrs) {
            @SuppressWarnings("unchecked")
            List<AclEntry> aclActual
                    = MapEntryUtils.isEmpty(attrs) ? null : (List<AclEntry>) attrs.get(IoUtils.ACL_VIEW_ATTR);
            if (getTestedVersion() > SftpConstants.SFTP_V3) {
                assertListEquals("Mismatched modifying ACL for file=" + path, aclExpected, aclActual);
            } else {
                assertNull("Unexpected modifying ACL for file=" + path, aclActual);
            }
        }

        @Override
        public void modifiedAttributes(
                ServerSession session, Path path, Map<String, ?> attrs, Throwable thrown) {
            @SuppressWarnings("unchecked")
            List<AclEntry> aclActual
                    = MapEntryUtils.isEmpty(attrs) ? null : (List<AclEntry>) attrs.get(IoUtils.ACL_VIEW_ATTR);
            if (getTestedVersion() > SftpConstants.SFTP_V3) {
                assertListEquals("Mismatched modified ACL for file=" + path, aclExpected, aclActual);
            } else {
                assertNull("Unexpected modified ACL for file=" + path, aclActual);
            }
        }
    });

    Path targetPath = detectTargetFolder();
    Path lclSftp = CommonTestSupportUtils.resolve(
            targetPath, SftpConstants.SFTP_SUBSYSTEM_NAME, getClass().getSimpleName());
    Files.createDirectories(lclSftp.resolve("sub-folder"));
    Path lclFile = assertHierarchyTargetFolderExists(lclSftp)
            .resolve(getCurrentTestName() + "-" + getTestedVersion() + ".txt");
    Files.write(lclFile, getClass().getName().getBytes(StandardCharsets.UTF_8));

    Path parentPath = targetPath.getParent();
    String remotePath = CommonTestSupportUtils.resolveRelativeRemotePath(parentPath, lclSftp);
    int numInvoked = 0;

    List<? extends SubsystemFactory> factories = sshd.getSubsystemFactories();
    sshd.setSubsystemFactories(Collections.singletonList(factory));
    try (ClientSession session = createAuthenticatedClientSession();
         SftpClient sftp = createSftpClient(session, getTestedVersion())) {
        for (DirEntry entry : sftp.readDir(remotePath)) {
            String fileName = entry.getFilename();
            if (".".equals(fileName) || "..".equals(fileName)) {
                continue;
            }

            Attributes attrs = validateSftpFileTypeAndPermissions(fileName, getTestedVersion(), entry.getAttributes());
            List<AclEntry> aclActual = attrs.getAcl();
            if (getTestedVersion() == SftpConstants.SFTP_V3) {
                assertNull("Unexpected ACL for entry=" + fileName, aclActual);
            } else {
                assertListEquals("Mismatched ACL for entry=" + fileName, aclExpected, aclActual);
            }

            attrs.getFlags().clear();
            attrs.setAcl(aclExpected);
            sftp.setStat(remotePath + "/" + fileName, attrs);
            if (getTestedVersion() > SftpConstants.SFTP_V3) {
                numInvoked++;
            }
        }
    } finally {
        sshd.setSubsystemFactories(factories);
    }

    assertEquals("Mismatched invocations count", numInvoked, numInvocations.get());
}

