private long toLocalTimeZone( long timestamp )
{
    String dateFormat = "dd-MM-yyyy hh:mm:ss a";
    DateFormat formatterWithTimeZone = new SimpleDateFormat( dateFormat );
    formatterWithTimeZone.setTimeZone( TimeZone.getTimeZone( "GMT" ) );
    String sDate = formatterWithTimeZone.format( new Date( timestamp ) );

    DateFormat formatter = new SimpleDateFormat( dateFormat );
    try
    {
        Date dateWithTimeZone = formatter.parse( sDate );
        return dateWithTimeZone.getTime();
    }
    catch ( ParseException e )
    {
        fail( "Date '" + sDate + "' can not be parsed!" );
        return 0L;
    }
}

private RemotingCommand updateConfig(ChannelHandlerContext ctx, RemotingCommand request) {
    if (ctx != null) {
        log.info("updateConfig called by {}", RemotingHelper.parseChannelRemoteAddr(ctx.channel()));
    }

    final RemotingCommand response = RemotingCommand.createResponseCommand(null);

    byte[] body = request.getBody();
    if (body != null) {
        String bodyStr;
        try {
            bodyStr = new String(body, MixAll.DEFAULT_CHARSET);
        } catch (UnsupportedEncodingException e) {
            log.error("updateConfig byte array to string error: ", e);
            response.setCode(ResponseCode.SYSTEM_ERROR);
            response.setRemark("UnsupportedEncodingException " + e);
            return response;
        }

        Properties properties = MixAll.string2Properties(bodyStr);
        if (properties == null) {
            log.error("updateConfig MixAll.string2Properties error {}", bodyStr);
            response.setCode(ResponseCode.SYSTEM_ERROR);
            response.setRemark("string2Properties error");
            return response;
        }

        if (properties.containsKey("kvConfigPath") || properties.containsKey("configStorePathName")) {
            response.setCode(ResponseCode.NO_PERMISSION);
            response.setRemark("Can not update config path");
            return response;
        }

        this.namesrvController.getConfiguration().update(properties);
    }

    response.setCode(ResponseCode.SUCCESS);
    response.setRemark(null);
    return response;
}

private Map<String, String> cleanAttributes(String elementName, Map<String, String> attributes)
{
    Map<String, String> cleanAttributes;

    if (this.htmlElementSanitizer == null || attributes == null) {
        cleanAttributes = attributes;
    } else {
        cleanAttributes = new LinkedHashMap<>();
        for (Map.Entry<String, String> e : attributes.entrySet()) {
            if (this.htmlElementSanitizer.isAttributeAllowed(elementName, e.getKey(), e.getValue())) {
                cleanAttributes.put(e.getKey(), e.getValue());
            } else {
                cleanAttributes.put(TRANSLATED_ATTRIBUTE_PREFIX + e.getKey(), e.getValue());
            }
        }
    }

    return cleanAttributes;
}

